properties.entry:

BEGIN

 --Log
 INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,null,'GeneracionExpedientes','comunes.idxdrop.PreparaGeneracionExpedientes','Inicio Generacion de expedientes',null);
 COMMIT;

END;


BEGIN
 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_DATOS_CNT');
 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_DATOS_GCL');
 --EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_DATOS_CNT';
 --EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_DATOS_GCL';
END;


Declare

   v_cadena     varchar2(50 char);
   v_proce      varchar2(50 char);
   v_paso       varchar2(50 char);
   v_nufilas    number;
   v_count      number;
   v_impPivote  varchar2(200 char);

Begin

   v_cadena := '';
   v_proce := 'GeneracionExpedientes';
   v_paso := 'comunes.create.PreparaGeneracionExpedientes';

   Begin
     EXECUTE IMMEDIATE 'Select PEN_VALOR From PEN_PARAM_ENTIDAD Where PEN_PARAM = ''importePivoteRecuperaciones'''
     INTO v_impPivote;

     EXCEPTION
      WHEN NO_DATA_FOUND
       THEN RAISE_APPLICATION_ERROR(-20000,'Importe pivote no informado en PEN_PARAM_ENTIDAD');
   End;

   --LOG
   INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'Se usa como importe Pivote: '||v_impPivote||'',null);
   COMMIT;


   --** Informacion de contratos
   Execute immediate ('
    Insert /*+append*/ INTO BATCH_SV_DATOS_CNT
      (cnt_id, ofi_id, cnt_riesgo, imp_pivote)
      Select /*+parallel*/ cnt.cnt_id
           , cnt.ofi_id
           , nvl(mov.mov_riesgo, 0) As cnt_riesgo
           , nvl(mov.'||v_impPivote||', 0) As imp_pivote
        From CNT_CONTRATOs cnt
        Join ${master.schema}.DD_ESC_ESTADO_CNT esc ON cnt.dd_esc_id = esc.dd_esc_id
          Join MOV_MOVIMIENTOS mov ON cnt.cnt_id = mov.cnt_id AND cnt.cnt_fecha_extraccion = mov.mov_fecha_extraccion
       Where cnt.borrado = 0
         And esc.dd_esc_codigo = ''0''
     ');

    --LOG
    V_NUFILAS:=SQL%ROWCOUNT;
    INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_DATOS_CNT Cargada',V_NUFILAS);
    COMMIT;


    --** Componentes de grupos
    ------------------------------
    Insert /*+append*/ INTO BATCH_SV_DATOS_GCL
    SELECT DISTINCT pgcl.gcl_id, pgcl.per_id
      FROM PER_GCL pgcl
      JOIN GCL_GRUPOS_CLIENTES gcl ON pgcl.gcl_id = gcl.gcl_id
      JOIN PER_PERSONAS per ON pgcl.per_id = per.per_id
     WHERE pgcl.borrado = 0
       AND gcl.borrado = 0;

    --LOG
    V_NUFILAS:=SQL%ROWCOUNT;
    INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_DATOS_GCL Cargada',V_NUFILAS);
    COMMIT;

End;


BEGIN
 Operacion_DDL.DDL_Table('STATS','BATCH_SV_DATOS_GCL');
 Operacion_DDL.DDL_Table('STATS','BATCH_SV_DATOS_CNT');
 --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_DATOS_GCL', estimate_percent => 20);
 --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_DATOS_CNT', estimate_percent => 20);
END;


BEGIN
 Operacion_DDL.DDL_INDEX('DROP','IDX_BATCH_SV_DATOS_CNT_EXP');
 Operacion_DDL.DDL_INDEX('DROP','IDX_BATCH_SV_DATOS_PER_EXP');
 --EXECUTE IMMEDIATE 'ALTER INDEX IDX_BATCH_SV_DATOS_CNT_EXP UNUSABLE';
 --EXECUTE IMMEDIATE 'ALTER INDEX IDX_BATCH_SV_DATOS_PER_EXP UNUSABLE';
END;


BEGIN

 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_DATOS_CNT_PER');
 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_DATOS_CNT_EXP');
 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_DATOS_PER_EXP');
 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_CLIENTES_BASE');
 Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SV_CLIENTES');
/*
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_DATOS_CNT_PER';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_DATOS_CNT_EXP';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_DATOS_PER_EXP';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_CLIENTES_BASE';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SV_CLIENTES';
*/
END;

DECLARE
v_cadena varchar2(50 char); v_proce varchar2(50 char); v_paso varchar2(50 char); v_nufilas number; v_count number;
BEGIN
v_cadena := ''; v_proce := 'GeneracionExpedientes'; v_paso := 'comunes.create.jobGeneracionExpedientes';
--** Relaciones personas-contratos validas ------------------------------------------- Insert /*+append*/ INTO BATCH_SV_DATOS_CNT_PER SELECT cpe.cnt_id, cpe.per_id, min(cpe.cpe_orden) AS cpe_orden, MAX(decode(dd_tin_titular,1,1,0)) as arrastre FROM CPE_CONTRATOS_PERSONAS cpe JOIN DD_TIN_TIPO_INTERVENCION tin ON cpe.dd_tin_id = tin.dd_tin_id WHERE cpe.borrado = 0 AND NOT (dd_tin_titular <> 1 AND dd_tin_avalista <> 1) --solo titulares GROUP BY cpe.cnt_id, cpe.per_id;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_DATOS_CNT_PER Cargada',V_NUFILAS); COMMIT;
--** Contratos que no se consideran por no estar libres --------------------------------------------------------- Insert /*+append*/ INTO BATCH_SV_DATOS_CNT_EXP -- Contratos en expedientes activos de recuperacion, seguimiento o gestion de deuda Select cex.exp_id, cex.cnt_id, tpx.dd_tpx_codigo From CEX_CONTRATOS_EXPEDIENTE cex Join EXP_EXPEDIENTES exp On cex.exp_id = exp.exp_id Join ${master.schema}.DD_EEX_ESTADO_EXPEDIENTE EEX On exp.dd_eex_id = eex.dd_eex_id Join DD_TPX_TIPO_EXPEDIENTE tpx On exp.dd_tpx_id = tpx.dd_tpx_id Where eex.dd_eex_codigo in ('1','2','4') --activo, bloqueado o congelado And tpx.dd_tpx_codigo = 'RECU' And eex.borrado = 0 And tpx.borrado = 0 And cex.borrado = 0 And exp.borrado = 0 UNION -- Litigios: Todos los contratos Select cex.exp_id, cex.cnt_id, dd_tpx_codigo From CEX_CONTRATOS_EXPEDIENTE cex Inner Join PRC_CEX pce On cex.cex_id = pce.cex_id Inner Join PRC_PROCEDIMIENTOS prc On pce.prc_id = prc.prc_id And prc.borrado = 0 Inner Join ${master.schema}.DD_EPR_ESTADO_PROCEDIMIENTO epr On prc.dd_epr_id = epr.dd_epr_id Inner Join EXP_EXPEDIENTES exp On cex.exp_id = exp.exp_id Inner Join DD_TPX_TIPO_EXPEDIENTE tpx On exp.dd_tpx_id = tpx.dd_tpx_id Inner Join ASU_ASUNTOS asu On exp.exp_id = asu.exp_id Inner Join ${master.schema}.DD_TAS_TIPOS_ASUNTO tas On asu.dd_tas_id = tas.dd_tas_id Where cex.borrado = 0 And epr.dd_epr_codigo in (${ddEstadoProcedimiento.propuesto.codigo}, ${ddEstadoProcedimiento.confirmado.codigo}, ${ddEstadoProcedimiento.aceptado.codigo}, ${ddEstadoProcedimiento.derivado.codigo}, ${ddEstadoProcedimiento.enConformacion.codigo}) And prc.prc_prc_id is null And tas.dd_tas_codigo <> '02' UNION -- Concursos: Solo contratos con riesgo directo de la persona concursada Select cex.exp_id, cex.cnt_id, dd_tpx_codigo From CEX_CONTRATOS_EXPEDIENTE cex Inner Join PRC_CEX pce On cex.cex_id = pce.cex_id Inner Join PRC_PROCEDIMIENTOS prc On pce.prc_id = prc.prc_id And prc.borrado = 0 Inner Join PRC_PER per On prc.prc_id = per.prc_id Inner Join CPE_CONTRATOS_PERSONAS cpe On per.per_id = cpe.per_id And cex.cnt_id = cpe.cnt_id And cpe.borrado = 0 Inner Join DD_TIN_TIPO_INTERVENCION tin On cpe.dd_tin_id = tin.dd_tin_id And tin.dd_tin_titular = 1 And tin.borrado = 0 Inner Join ${master.schema}.DD_EPR_ESTADO_PROCEDIMIENTO epr On prc.dd_epr_id = epr.dd_epr_id Inner Join EXP_EXPEDIENTES exp On cex.exp_id = exp.exp_id Inner Join DD_TPX_TIPO_EXPEDIENTE tpx On exp.dd_tpx_id = tpx.dd_tpx_id Inner Join ASU_ASUNTOS asu On exp.exp_id = asu.exp_id Inner Join ${master.schema}.DD_TAS_TIPOS_ASUNTO tas On asu.dd_tas_id = tas.dd_tas_id Where cex.borrado = 0 And prc.prc_prc_id is null And epr.dd_epr_codigo in (${ddEstadoProcedimiento.propuesto.codigo}, ${ddEstadoProcedimiento.confirmado.codigo}, ${ddEstadoProcedimiento.aceptado.codigo}, ${ddEstadoProcedimiento.derivado.codigo}, ${ddEstadoProcedimiento.enConformacion.codigo}) And tas.dd_tas_codigo = '02' And not exists (select exr.exp_id from EXR_EXPEDIENTE_RECOBRO exr where cex.exp_id = exr.exp_id);
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_DATOS_CNT_EXP Cargada',V_NUFILAS); COMMIT;
--** Personas en expedientes de recuperacion, seguimiento o gestion de deuda ----------------------------------------------------------------------------- Insert /*+append*/ INTO BATCH_SV_DATOS_PER_EXP Select pex.exp_id, pex.per_id, tpx.dd_tpx_codigo From PEX_PERSONAS_EXPEDIENTE pex Join EXP_EXPEDIENTES exp On pex.exp_id = exp.exp_id Join ${master.schema}.DD_EEX_ESTADO_EXPEDIENTE EEX On exp.dd_eex_id = eex.dd_eex_id Join DD_TPX_TIPO_EXPEDIENTE tpx On exp.dd_tpx_id = tpx.dd_tpx_id Where eex.dd_eex_codigo in ('1','2','4') --activo, bloqueado o congelado And tpx.dd_tpx_codigo in ('SEG','GESDEU') And pex.borrado = 0 And exp.borrado = 0 And eex.borrado = 0 And tpx.borrado = 0;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_DATOS_PER_EXP Cargada',V_NUFILAS); COMMIT;
--** Posibles clientes a expedientar ------------------------------------- Insert /*+append*/ INTO BATCH_SV_CLIENTES_BASE WITH FECHASESTADOS AS (Select sysdate - ((((sum(est.est_plazo)/1000)/24)/60)/60) fecha, arq_id, arq_prioridad From ARQ_ARQUETIPOS arq Inner Join ITI_ITINERARIOS iti On arq.iti_id = iti.iti_id Inner Join EST_ESTADOS est On iti.iti_id = est.iti_id Inner Join ${master.schema}.DD_EST_ESTADOS_ITINERARIOS dd_est On est.dd_est_id = dd_est.dd_est_id --sumamos todos los plazos que esten por debajo de gestionvencidos And dd_est.dd_est_orden <= (select dd_est_orden from ${master.schema}.DD_EST_ESTADOS_ITINERARIOS where dd_est_codigo = 'GV') --solo los de la entidad cliente Inner Join ${master.schema}.DD_EIN_ENTIDAD_INFORMACION dd_ein On dd_est.dd_ein_id = dd_ein.dd_ein_id And dd_ein_codigo = '1' Where arq.borrado = 0 --agrupamos por arquetipo para poder calcular el plazo del itinerario de los clientes Group by arq.arq_id, arq.arq_prioridad) SELECT cli.cli_id, cli.per_id, cli.arq_id, arq_prioridad FROM FECHASESTADOS vFechas INNER JOIN CLI_CLIENTES cli ON vFechas.arq_id = cli.arq_id --Solo los que esten en gestionVencidos INNER JOIN ${master.schema}.DD_EST_ESTADOS_ITINERARIOS dd_est ON cli.dd_est_id = dd_est.dd_est_id AND dd_est_codigo = 'GV' INNER JOIN ${master.schema}.DD_EIN_ENTIDAD_INFORMACION dd_ein ON dd_est.dd_ein_id = dd_ein.dd_ein_id AND dd_ein_codigo = '1' WHERE cli.borrado = 0 AND to_date(cli.cli_fecha_creacion,'DD/MM/RR') <= to_date(vfechas.fecha,'DD/MM/RR') --Que esten fuera de plazo del itinerario /* Ordenamos por prioridad del arquetipo para crear primero expedientes a los clientes con arq. mas prioritarios y desechar a las personas relacionadas menos prioritarias */ --ORDER BY arq_prioridad ;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_CLIENTES_BASE Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SV_CLIENTES_BASE'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_CLIENTES_BASE', estimate_percent => 20);
--** Buscamos sus umbrales y tipo de itinerarios (recuperacion, seguimiento o gestion de deuda) ------------------------------------------------------------------------------------------------ Insert /*+append*/ INTO BATCH_SV_CLIENTES Select /*+parallel*/ DISTINCT cli.cli_id, cli.per_id, cli.arq_id, cli.arq_prioridad , per.per_nombre, per.per_apellido1, per.per_apellido2 , aex.dd_aex_codigo, iti.iti_nombre, tit.dd_tit_codigo From BATCH_SV_CLIENTES_BASE cli , PER_PERSONAS per , (select per_id, arq_id, row_number() over (partition by per_id order by arq_date desc) as arq_orden from ARR_ARQ_RECUPERACION_PERSONA) arr , ARQ_ARQUETIPOS arq , ITI_ITINERARIOS iti , ${master.schema}.DD_TIT_TIPO_ITINERARIOS tit , ${master.schema}.DD_AEX_AMBITOS_EXPEDIENTE aex Where cli.per_id = per.per_id And per.per_id = arr.per_id And per.borrado = 0 And arr.arq_id = arq.arq_id And arq.borrado = 0 And arq.iti_id = iti.iti_id And iti.borrado = 0 And iti.dd_tit_id = tit.dd_tit_id And tit.borrado = 0 And iti.dd_aex_id = aex.dd_aex_id And aex.borrado = 0 And tit.dd_tit_codigo in ('REC','SIS','SIN','DEU') -- No este afectado por umbrales And nvl(per.PER_EXP_UMBRAL_FECHA, to_date('19000101','yyyymmdd')) < SYSDATE --fecha unmbral ha pasado And nvl(per.PER_EXP_UMBRAL_IMPORTE, -1) < nvl(per.PER_RIESGO_DIR_VENCIDO,0) --importe vencido superior a importe umbral And arr.arq_orden = 1;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SV_CLIENTES Cargada',V_NUFILAS); COMMIT; END;


BEGIN
 Operacion_DDL.DDL_INDEX('CREATE','IDX_BATCH_SV_DATOS_CNT_EXP');
 Operacion_DDL.DDL_INDEX('CREATE','IDX_BATCH_SV_DATOS_PER_EXP');
 --EXECUTE IMMEDIATE 'ALTER INDEX IDX_BATCH_SV_DATOS_CNT_EXP REBUILD';
 --EXECUTE IMMEDIATE 'ALTER INDEX IDX_BATCH_SV_DATOS_PER_EXP REBUILD';

 Operacion_DDL.DDL_Table('STATS','BATCH_SV_CLIENTES');
 Operacion_DDL.DDL_Table('STATS','BATCH_SV_DATOS_PER_EXP');
 Operacion_DDL.DDL_Table('STATS','BATCH_SV_DATOS_CNT_PER');
 Operacion_DDL.DDL_Table('STATS','BATCH_SV_DATOS_CNT_EXP');
 --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_CLIENTES', estimate_percent => 20);
 --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_DATOS_PER_EXP', estimate_percent => 20);
 --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_DATOS_CNT_PER', estimate_percent => 20);
 --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SV_DATOS_CNT_EXP', estimate_percent => 20);
END;


Begin

 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_GUIA');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_TIT');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_GCL');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_BASE');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_1G_PER');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_1G_CNT');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_1GEN');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_2G_PER');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_2G_CNT');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_TODO');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_EXP_DES');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA_TODO_LM');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_SEG_DATOS_SALIDA');

 /*
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_GUIA';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_TIT';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_GCL';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_BASE';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_1G_PER';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_1G_CNT';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_1GEN';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_2G_PER';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_2G_CNT';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_TODO';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_EXP_DES';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA_TODO_LM';
 EXECUTE IMMEDIATE 'TRUNCATE TABLE BATCH_SEG_DATOS_SALIDA';
 */
End;

DECLARE
v_count number; v_limit_per number; v_limit_cnt number;
v_cadena varchar2(50 char); v_proce varchar2(50 char); v_paso varchar2(50 char); v_nufilas number;
BEGIN
v_cadena := ''; v_proce := 'GeneracionExpedientes'; v_paso := 'seguimiento.create.GeneracionExpedientes';
--** Filtramos personas ya en exp de seguimiento o gestion de deuda -- y buscamos contratos de pase con riesgo -------------------------------------------------------------------- Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_GUIA SELECT s_exp_expedientes.nextval as EXP_ID , t.* FROM( select trim(cli.PER_NOMBRE||' '||cli.PER_APELLIDO1||' '||cli.PER_APELLIDO2) as EXP_DESCRIPCION , cli.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cnt.CNT_ID , cli.PER_ID , cpe.CPE_ORDEN , cli.ARQ_ID , 1 as CEX_PASE , 1 as PEX_PASE , cli.dd_tit_codigo , 1 as ARRASTRE From BATCH_SV_CLIENTES cli , BATCH_SV_DATOS_PER_EXP pex , CCL_CONTRATOS_CLIENTE ccl , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_CNT cnt Where cli.per_id = pex.per_id(+) And pex.exp_id is null --libre And cli.cli_id = ccl.cli_id And ccl.cnt_id = cnt.cnt_id And ccl.ccl_pase = 1 And cli.per_id = cpe.per_id And ccl.cnt_id = cpe.cnt_id And ccl.borrado = 0 And cli.dd_tit_codigo in ('SIS','SIN','DEU') Order by cli.arq_prioridad , cpe.cpe_orden , cnt.cnt_riesgo desc ) t;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_GUIA Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_GUIA'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_GUIA', estimate_percent => 20);
--** Obtenemos resto de contratos de la persona de pase ------------------------------------------------------- Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_TIT Select distinct gui.EXP_ID , gui.EXP_DESCRIPCION , gui.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cpe.CNT_ID , cpe.PER_ID , cpe.CPE_ORDEN , gui.ARQ_ID , DECODE(gui.CNT_ID, cpe.CNT_ID ,1,0) as CEX_PASE , DECODE(gui.PER_ID, cpe.PER_ID ,1,0) as PEX_PASE , gui.dd_tit_codigo , cpe.ARRASTRE From BATCH_SEG_DATOS_SALIDA_GUIA gui , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_CNT cnt Where gui.PER_ID = cpe.PER_ID And gui.CNT_ID <> cpe.CNT_ID And cpe.CNT_ID = cnt.CNT_ID;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_TIT Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_TIT'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_TIT', estimate_percent => 20);
--** Obtenemos otras personas del grupo de la persona de pase y sus contratos ------------------------------------------------------------------------------ Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_GCL Select distinct gui.EXP_ID , gui.EXP_DESCRIPCION , gui.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cpe.CNT_ID , cpe.PER_ID , cpe.CPE_ORDEN , gui.ARQ_ID , DECODE(gui.CNT_ID, cpe.CNT_ID ,1 ,0) as CEX_PASE , DECODE(gui.PER_ID, cpe.PER_ID ,1 ,0) as PEX_PASE From BATCH_SEG_DATOS_SALIDA_GUIA gui , BATCH_SV_DATOS_GCL gl1 , BATCH_SV_DATOS_GCL gl2 , BATCH_SV_DATOS_PER_EXP pex , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_CNT cnt Where gui.PER_ID = gl1.PER_ID And gl1.GCL_ID = gl2.GCL_ID And gl2.PER_ID = pex.PER_ID(+) and pex.exp_id is null --libre And gl2.PER_ID = cpe.PER_ID And gui.PER_ID <> cpe.PER_ID And cpe.CNT_ID = cnt.CNT_ID And gui.DD_AEX_CODIGO in ('PG','PPGRA', 'PSGRA');
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_GCL Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_GCL'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_GCL', estimate_percent => 20);
--** Juntamos todas las relaciones ---------------------------------- Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_BASE SELECT EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE, MIN(PRIO) as PRIO FROM (Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE, 1 as PRIO From BATCH_SEG_DATOS_SALIDA_GUIA UNION ALL Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE, 2 as PRIO From BATCH_SEG_DATOS_SALIDA_TIT) GROUP BY EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_BASE Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_BASE'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_BASE', estimate_percent => 20);
--** Inicio Primera Generacion si hace falta ---------------------------------------------
-- a) Buscamos otros titulares de los contratos de los que es titular la persona de pase. Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_1G_PER Select distinct gui.EXP_ID , gui.EXP_DESCRIPCION , gui.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cpe.CNT_ID , cpe.PER_ID , cpe.CPE_ORDEN , gui.ARQ_ID , DECODE(pas.CNT_ID, cpe.CNT_ID ,1 ,0) as CEX_PASE , DECODE(pas.PER_ID, cpe.PER_ID ,1 ,0) as PEX_PASE From BATCH_SEG_DATOS_SALIDA_BASE gui , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_PER_EXP pex , BATCH_SV_DATOS_CNT cnt , BATCH_SEG_DATOS_SALIDA_GUIA pas Where gui.CNT_ID = cpe.CNT_ID And gui.PER_ID <> cpe.PER_ID and cpe.ARRASTRE = 1 And cpe.PER_ID = pex.PER_ID(+) and pex.EXP_ID is null --libre And cpe.CNT_ID = cnt.CNT_ID And gui.EXP_ID = pas.EXP_ID And gui.DD_AEX_CODIGO in ('PPGRA', 'PSGRA') And gui.ARRASTRE = 1 ;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_1G_PER Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_1G_PER'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_1G_PER', estimate_percent => 20);
--b) Buscamos otros contratos de los que sean titulares las personas del paso a). Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_1G_CNT SELECT distinct gui.EXP_ID , gui.EXP_DESCRIPCION , gui.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cpe.CNT_ID , cpe.PER_ID , cpe.CPE_ORDEN , gui.ARQ_ID , DECODE(pas.CNT_ID, cpe.CNT_ID ,1 ,0) as CEX_PASE , DECODE(pas.PER_ID, cpe.PER_ID ,1 ,0) as PEX_PASE FROM BATCH_SEG_DATOS_SALIDA_1G_PER gui , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_CNT cnt , BATCH_SEG_DATOS_SALIDA_GUIA pas WHERE gui.PER_ID = cpe.PER_ID AND gui.CNT_ID <> cpe.CNT_ID and cpe.ARRASTRE = 1 AND cpe.CNT_ID = cnt.CNT_ID AND gui.EXP_ID = pas.EXP_ID AND gui.DD_AEX_CODIGO in ('PPGRA', 'PSGRA') AND pas.CEX_PASE = 1 and pas.PEX_PASE = 1;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_1G_CNT Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_1G_CNT'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_1G_CNT', estimate_percent => 20);
--** Juntamos todas las relaciones (intervinientes + arrastrados 1Gen) ---------------------------------------------------------------------------------- Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_1GEN SELECT EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE, MIN(PRIO) as PRIO FROM (Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE, PRIO From BATCH_SEG_DATOS_SALIDA_BASE UNION ALL Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, 1 as ARRASTRE, 4 as PRIO From BATCH_SEG_DATOS_SALIDA_1G_PER UNION ALL Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, 1 as ARRASTRE, 5 as PRIO From BATCH_SEG_DATOS_SALIDA_1G_CNT) GROUP BY EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, ARRASTRE;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_1GEN Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_1GEN'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_1GEN', estimate_percent => 20);
--** Inicio Segunda Generacion si hace falta ---------------------------------------------
-- a) Buscamos otros titulares de los contratos del expediente. Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_2G_PER Select distinct gui.EXP_ID , gui.EXP_DESCRIPCION , gui.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cpe.CNT_ID , cpe.PER_ID , cpe.CPE_ORDEN , gui.ARQ_ID , DECODE(pas.CNT_ID, cpe.CNT_ID ,1 ,0) as CEX_PASE , DECODE(pas.PER_ID, cpe.PER_ID ,1 ,0) as PEX_PASE From BATCH_SEG_DATOS_SALIDA_1GEN gui , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_PER_EXP pex , BATCH_SV_DATOS_CNT cnt , BATCH_SEG_DATOS_SALIDA_GUIA pas Where gui.CNT_ID = cpe.CNT_ID And gui.PER_ID <> cpe.PER_ID and cpe.ARRASTRE = 1 And cpe.PER_ID = pex.PER_ID(+) and pex.EXP_ID is null --libre And cpe.CNT_ID = cnt.CNT_ID And gui.EXP_ID = pas.EXP_ID And gui.DD_AEX_CODIGO = 'PSGRA' And gui.ARRASTRE = 1;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_2G_PER Cargada',V_NUFILAS); COMMIT;
--b) Buscamos otros contratos de los que sean titulares las personas del paso a). Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_2G_CNT SELECT distinct gui.EXP_ID , gui.EXP_DESCRIPCION , gui.DD_AEX_CODIGO , cnt.OFI_ID , cnt.CNT_RIESGO , cpe.CNT_ID , cpe.PER_ID , cpe.CPE_ORDEN , gui.ARQ_ID , DECODE(pas.CNT_ID, cpe.CNT_ID ,1 ,0) as CEX_PASE , DECODE(pas.PER_ID, cpe.PER_ID ,1 ,0) as PEX_PASE FROM BATCH_SEG_DATOS_SALIDA_2G_PER gui , BATCH_SV_DATOS_CNT_PER cpe , BATCH_SV_DATOS_CNT cnt , BATCH_SEG_DATOS_SALIDA_GUIA pas WHERE gui.PER_ID = cpe.PER_ID AND gui.CNT_ID <> cpe.CNT_ID and cpe.ARRASTRE = 1 AND cpe.CNT_ID = cnt.CNT_ID AND gui.EXP_ID = pas.EXP_ID AND gui.DD_AEX_CODIGO = 'CSGRA' And pas.CEX_PASE = 1 and pas.PEX_PASE = 1;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_2G_CNT Cargada',V_NUFILAS); COMMIT;
--** Juntamos todas las relaciones ----------------------------------- Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_TODO SELECT EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, MIN(PRIO) as PRIO FROM (Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, PRIO From BATCH_SEG_DATOS_SALIDA_1GEN UNION Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, 3 as PRIO From BATCH_SEG_DATOS_SALIDA_GCL UNION Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, 6 as PRIO From BATCH_SEG_DATOS_SALIDA_2G_PER UNION Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, 7 as PRIO From BATCH_SEG_DATOS_SALIDA_2G_CNT) GROUP BY EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE ;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_TODO Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA_TODO'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA_TODO', estimate_percent => 20);
--** Limpiamos relaciones arrastradas en varios expedientes ------------------------------------------------------------
-- obtenemos valor parametrizado del limite
-- obtenemos valor parametrizado del limite Select PEN_VALOR into v_limit_per From PEN_PARAM_ENTIDAD WHERE pen_param = 'expediente.obtenerPersonasAdicionales.limite'; Select PEN_VALOR into v_limit_cnt From PEN_PARAM_ENTIDAD WHERE pen_param = 'expediente.obtenerContratosAdicionales.limite';
v_count := 1; while v_count > 0 loop
-- Quitamos relaciones obtenidas (ninguna la primera vez) Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SEG_DATOS_SALIDA_TODO_LM'); Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_TODO_LM WITH BATCH_SEG_FILTRADOS AS (Select t.EXP_ID, t.EXP_DESCRIPCION, t.DD_AEX_CODIGO, t.OFI_ID, t.CNT_RIESGO, t.CNT_ID, t.PER_ID, t.CPE_ORDEN, t.ARQ_ID, t.CEX_PASE, t.PEX_PASE, t.PRIO From BATCH_SEG_DATOS_SALIDA_TODO t , BATCH_SEG_DATOS_SALIDA_EXP_DES d Where t.exp_id = d.exp_id(+) And t.per_id = d.per_id(+) And d.exp_id is null) Select EXP_ID, EXP_DESCRIPCION, DD_AEX_CODIGO, OFI_ID, CNT_RIESGO, CNT_ID, PER_ID, CPE_ORDEN, ARQ_ID, CEX_PASE, PEX_PASE, PRIO -- recalculamos posicion de personas y contratos dentro de cada expediente , dense_rank() over (partition by exp_id order by cex_pase desc, prio, cnt_riesgo desc, cnt_id) as num_cnt , dense_rank() over (partition by exp_id order by pex_pase desc, prio, cpe_orden, per_id) as num_per From BATCH_SEG_FILTRADOS;
--Log v_nufilas := SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_TODO_LM Borrado',V_NUFILAS); Commit;
-- Obtenemos relaciones a eliminar Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA_EXP_DES (EXP_ID,PER_ID,PEX_PASE) WITH BATCH_SEG_DISTRIBUCION AS (Select distinct per_id, exp_id, min(num_per) as num_per, pex_pase From BATCH_SEG_DATOS_SALIDA_TODO_LM Where num_cnt <= v_limit_cnt Group by per_id, exp_id, pex_pase) Select distinct ko.exp_id, ko.per_id, ko.pex_pase From BATCH_SEG_DISTRIBUCION ko, BATCH_SEG_DISTRIBUCION ok Where ko.per_id = ok.per_id And ko.exp_id > ok.exp_id And ok.num_per <= v_limit_per; --40;
v_count := SQL%ROWCOUNT; v_nufilas := v_count; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_TODO_LM Borrado',V_NUFILAS); Commit;
-- Si la relacion a eliminar incluye persona de pase, descartamos todo el expediente delete BATCH_SEG_DATOS_SALIDA_TODO a where exists (select distinct exp_id from BATCH_SEG_DATOS_SALIDA_EXP_DES b where a.exp_id = b.exp_id and b.pex_pase=1); --Log v_nufilas := SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_TODO_LM Borrado',V_NUFILAS); Commit;
end loop;
-- dejamos limpia la temporal --execute immediate 'Truncate table BATCH_SEG_DATOS_SALIDA_EXP_DES'; Operacion_DDL.DDL_Table('TRUNCATE','BATCH_SEG_DATOS_SALIDA_EXP_DES'); --Log INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA_EXP_DES Truncada',V_NUFILAS); Commit;
--** Aplicamos limites para todo lo que queda ---------------------------------------------- Insert /*+append*/ into BATCH_SEG_DATOS_SALIDA (EXP_ID,EXP_DESCRIPCION,EXP_MANUAL,DD_AEX_CODIGO,DD_EEX_ID,DD_EST_ID,EXP_FECHA_EST_ID,OFI_ID,CNT_ID,PER_ID,ARQ_ID,CEX_PASE,PEX_PASE,DD_TPX_CODIGO) SELECT f.EXP_ID , f.EXP_DESCRIPCION , 0 as EXP_MANUAL , f.DD_AEX_CODIGO , 2 as DD_EEX_ID , 3 as DD_EST_ID , sysdate as EXP_FECHA_EST_ID , f.OFI_ID , f.CNT_ID , f.PER_ID , f.ARQ_ID , f.CEX_PASE , f.PEX_PASE , decode(g.dd_tit_codigo,'SIS','SEG','SIN','SEG','DEU','GESDEU') as dd_tpx_codigo FROM BATCH_SEG_DATOS_SALIDA_TODO_LM f , BATCH_SEG_DATOS_SALIDA_GUIA g WHERE f.EXP_ID = g.EXP_ID AND f.NUM_PER <= v_limit_per AND f.NUM_CNT <= v_limit_cnt;
--LOG V_NUFILAS:=SQL%ROWCOUNT; INSERT INTO BATCH_LOG VALUES(SYSTIMESTAMP,V_CADENA,V_PROCE,V_PASO,'BATCH_SEG_DATOS_SALIDA Cargada',V_NUFILAS); COMMIT;
Operacion_DDL.DDL_Table('STATS','BATCH_SEG_DATOS_SALIDA'); --DBMS_STATS.GATHER_TABLE_STATS (ownname => USER , tabname => 'BATCH_SEG_DATOS_SALIDA', estimate_percent => 20); END;


Begin

 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_VEN_DATOS_SALIDA_GUIA');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_VEN_DATOS_SALIDA_TIT');
 Operacion_DDL.DDL_TABLE('TRUNCATE','BATCH_VEN_DATOS_SALIDA_GCL');
