DECLARE
CURSOR c_UPDATE IS SELECT CNT_ID, TMP_CNT_ID FROM TMP_CNT_CONTRATOS A, CNT_CONTRATOS B WHERE A.TMP_CNT_CODIGO_CNT50 = B.CNT_CONTRATO AND NVL(TMP_CNT_NUM_CNT_ANTERIOR,0) != 0 AND TMP_CNT_NUM_CNT_ANTERIOR <> TMP_CNT_CONTRATO;
CURSOR CODS_CAMPOS IS SELECT DD_IFC_CODIGO, CASE DD_IFC_CODIGO WHEN 'CNT_COD_PROPIETARIO' THEN 'TMP_CNT_CODIGO_PROPIETARIO' WHEN 'CNT_TIPO_PRODUCTO' THEN 'TMP_CNT_TIPO_PRODUCTO' WHEN 'NUMERO_CONTRATO' THEN 'TMP_CNT_CONTRATO' WHEN 'NUMERO_ESPEC' THEN 'TMP_CNT_NUM_ESPEC' END AS COLUMN_TMP FROM EXT_DD_IFC_INFO_CONTRATO WHERE DD_IFC_CODIGO IN ('CNT_COD_PROPIETARIO','CNT_TIPO_PRODUCTO','NUMERO_CONTRATO','NUMERO_ESPEC');
v_SQL VARCHAR2(32000);
BEGIN
FOR k IN CODS_CAMPOS LOOP
FOR i IN c_UPDATE LOOP
v_SQL := 'UPDATE EXT_IAC_INFO_ADD_CONTRATO SET IAC_VALUE = (SELECT TRIM('||k.COLUMN_TMP||') FROM TMP_CNT_CONTRATOS WHERE TMP_CNT_ID = '||i.TMP_CNT_ID||'), USUARIOMODIFICAR = ''BA-PasProd'', FECHAMODIFICAR = SYSTIMESTAMP WHERE CNT_ID = '||i.CNT_ID||' AND DD_IFC_ID = (SELECT DD_IFC_ID FROM EXT_DD_IFC_INFO_CONTRATO WHERE DD_IFC_CODIGO='||''''||k.DD_IFC_CODIGO||''''||')';
EXECUTE IMMEDIATE v_SQL;
END LOOP;
END LOOP;
FOR j IN CODS_CAMPOS LOOP
v_SQL := 'INSERT /*+ APPEND PARALLEL(EXT_IAC_INFO_ADD_CONTRATO, 16) PQ_DISTRIBUTE(EXT_IAC_INFO_ADD_CONTRATO, NONE) */ INTO EXT_IAC_INFO_ADD_CONTRATO (IAC_ID,DD_IFC_ID,CNT_ID,IAC_VALUE,USUARIOCREAR,FECHACREAR,BORRADO) SELECT S_EXT_IAC_INFO_ADD_CONTRATO.NEXTVAL AS IAC_ID, (SELECT DD_IFC_ID FROM EXT_DD_IFC_INFO_CONTRATO WHERE DD_IFC_CODIGO='||''''||j.DD_IFC_CODIGO||''''||') DD_IFC_ID, CNT.CNT_ID, TRIM('||j.COLUMN_TMP||'), ''BA-PasProd'', SYSTIMESTAMP, 0 FROM TMP_CNT_CONTRATOS TMP, (SELECT CNT_ID, CNT_CONTRATO FROM CNT_CONTRATOS) CNT WHERE CNT.CNT_CONTRATO = TMP.TMP_CNT_CODIGO_CNT50 AND TMP.'||j.COLUMN_TMP||' IS NOT NULL AND NOT EXISTS ( SELECT 1 FROM EXT_IAC_INFO_ADD_CONTRATO IAC WHERE IAC.CNT_ID = CNT.CNT_ID AND IAC.DD_IFC_ID = (SELECT DD_IFC_ID FROM EXT_DD_IFC_INFO_CONTRATO WHERE DD_IFC_CODIGO='||''''||j.DD_IFC_CODIGO||''''||') AND CNT.CNT_CONTRATO = TMP.TMP_CNT_CODIGO_CNT50 )';
EXECUTE IMMEDIATE v_SQL;
END LOOP;
END;
