    	DECLARE
    		V_SQL VARCHAR(2000);
    	BEGIN
        	V_SQL := 'ANALYZE TABLE TMP_RECOBRO_DETALLE_FACTURA COMPUTE STATISTICS';
        	EXECUTE IMMEDIATE V_SQL;
        	COMMIT;

    		INSERT /*+ APPEND PARALLEL(H_RECOBRO_DETALLE_FACTURA, 16) PQ_DISTRIBUTE(H_RECOBRO_DETALLE_FACTURA, NONE) */ INTO H_RECOBRO_DETALLE_FACTURA 
		                SELECT TRUNC(SYSDATE) AS FECHA_HIST, S_H_RECOBRO_DETALLE_FACTURA.NEXTVAL AS H_RDF_ID, T.* FROM TMP_RECOBRO_DETALLE_FACTURA T;
	    	COMMIT;

        	V_SQL := 'ANALYZE TABLE H_RECOBRO_DETALLE_FACTURA COMPUTE STATISTICS';
        	EXECUTE IMMEDIATE V_SQL;        
		END;


