    declare
        v_sql VARCHAR2(2000 CHAR);
        v_codigo_extincion VARCHAR2(5 CHAR);
        v_codigo_desactivado VARCHAR2(5 CHAR);
        v_codigo_expediente_activo VARCHAR2(5 CHAR);
    begin
        v_codigo_extincion:= ${ddEstadoEsquemas.Inactivo.codigo};
        v_codigo_desactivado:= ${ddEstadoEsquemas.Desactivado.codigo};
        v_codigo_expediente_activo:= ${ddEstadoExpedientes.Activo.codigo};
        v_sql:='
            UPDATE RCF_ESQ_ESQUEMA ESQ SET ESQ.RCF_DD_EES_ID = (SELECT EES1.RCF_DD_EES_ID FROM RCF_DD_EES_ESTADO_ESQUEMA EES1
            WHERE EES1.RCF_DD_EES_CODIGO = :v_codigo_desactivado),
            ESQ.RCF_FECHA_DESACT = SYSDATE WHERE ESQ.RCF_ESQ_ID IN (
            SELECT ESQ1.RCF_ESQ_ID FROM RCF_ESQ_ESQUEMA ESQ1
            JOIN RCF_DD_EES_ESTADO_ESQUEMA EES ON ESQ1.RCF_DD_EES_ID = EES.RCF_DD_EES_ID
            WHERE EES.RCF_DD_EES_CODIGO = :v_codigo_extincion AND (SELECT COUNT(DISTINCT EXPE.EXP_ID)
            FROM BATCH_DATOS_EXP EXPE WHERE EXPE.RCF_ESQ_ID = ESQ1.RCF_ESQ_ID AND EXPE.DD_EEX_CODIGO = :v_codigo_expediente_activo) = 0)
        ';
        execute immediate v_sql using v_codigo_desactivado, v_codigo_extincion, v_codigo_expediente_activo;
    COMMIT;
    end;

declare begin UPDATE CRC_CICLO_RECOBRO_CNT SET CRC_FECHA_BAJA = TRUNC(SYSDATE) , FECHAMODIFICAR = SYSDATE , USUARIOMODIFICAR = '${usuario.crear}' WHERE CRC_ID IN ( SELECT CRC_ID FROM CRC_CICLO_RECOBRO_CNT CRC JOIN CRE_CICLO_RECOBRO_EXP CRE ON CRC.CRE_ID = CRE.CRE_ID JOIN TMP_REC_EXP_CANCELADOS TMP ON CRE.EXP_ID = TMP.EXP_ID WHERE CRC.CRC_FECHA_BAJA IS NULL AND CRC.BORRADO = 0 );
UPDATE CRP_CICLO_RECOBRO_PER SET CRP_FECHA_BAJA = TRUNC(SYSDATE) , FECHAMODIFICAR = SYSDATE , USUARIOMODIFICAR = '${usuario.crear}' WHERE CRP_ID IN ( SELECT CRP_ID FROM CRP_CICLO_RECOBRO_PER CRP JOIN CRE_CICLO_RECOBRO_EXP CRE ON CRP.CRE_ID = CRE.CRE_ID JOIN TMP_REC_EXP_CANCELADOS TMP ON CRE.EXP_ID = TMP.EXP_ID WHERE CRP.CRP_FECHA_BAJA IS NULL AND CRP.BORRADO = 0 );
UPDATE CRE_CICLO_RECOBRO_EXP SET CRE_FECHA_BAJA = TRUNC(SYSDATE) , FECHAMODIFICAR = SYSDATE , USUARIOMODIFICAR = '${usuario.crear}' WHERE CRE_ID IN ( SELECT CRE_ID FROM CRE_CICLO_RECOBRO_EXP CRE JOIN TMP_REC_EXP_CANCELADOS TMP ON CRE.EXP_ID = TMP.EXP_ID WHERE CRE.CRE_FECHA_BAJA IS NULL AND CRE.BORRADO = 0 );
UPDATE EXP_EXPEDIENTES SET DD_EEX_ID = ${ddEstadoExpedientes.Cancelado.id}, USUARIOMODIFICAR = '${usuario.crear}', FECHAMODIFICAR = SYSDATE WHERE EXP_ID IN (SELECT EXP_ID FROM TMP_REC_EXP_CANCELADOS); end;

        
        /*
         * CREACIÓN DE EXPEDIENTES NUEVOS
         */
        declare
          /*
           * CONSTANTES
           */
           cUSUARIO_BATCH VARCHAR2(10 CHAR);
           cARQ_GENERICO NUMBER;
           cEEX_ID NUMBER;
           cMASTER_SCHEMA VARCHAR2(10 CHAR);

           /*
            * CURSORES
            */
          CURSOR curEXP_NUEVOS IS SELECT DISTINCT EXP_ID, EXP_DESCRIPCION, OFI_ID, EXP_MANUAL FROM BATCH_DATOS_SALIDA
          WHERE CEX_PASE = 1 AND EXP_ID NOT IN (SELECT EXP_ID FROM EXR_EXPEDIENTE_RECOBRO);
          rEXP curEXP_NUEVOS%ROWTYPE;

          CURSOR curDATOS_CEX (pEXP_ID NUMBER) IS SELECT DISTINCT EXP_ID, CNT_ID, DD_AEX_CODIGO, CEX_PASE FROM BATCH_DATOS_SALIDA WHERE EXP_ID = pEXP_ID;
          rDATOS_CEX curDATOS_CEX%ROWTYPE;

          CURSOR curDATOS_PEX (pEXP_ID NUMBER) IS SELECT DISTINCT EXP_ID, PER_ID, PEX_PASE FROM BATCH_DATOS_SALIDA WHERE EXP_ID = pEXP_ID;
          rDATOS_PEX curDATOS_PEX%ROWTYPE;
          /*
           * VARIABLES
           */
          vCEX_ID NUMBER(16);
          vAEX_ID NUMBER(16);
          vPEX_ID NUMBER(16);
          vDD_TPX_ID NUMBER(16);
          vDD_EST_ID NUMBER(16);
          nCount NUMBER;
        begin

              /*
               * CONSTANTES
               */
              cUSUARIO_BATCH := '${usuario.crear}';
              cARQ_GENERICO := ${id.arquetipo.generico};
              cMASTER_SCHEMA := '${master.schema}';
              cEEX_ID := ${ddEstadoExpedientes.Activo.id};

              /* INICIO BUCLE NUEVOS EXPEDIENTES */
              IF NOT curEXP_NUEVOS%ISOPEN THEN
                  OPEN curEXP_NUEVOS;
              END IF;
              LOOP
                FETCH curEXP_NUEVOS INTO rEXP;
                EXIT WHEN curEXP_NUEVOS%NOTFOUND;

                /* INICIO INSERTAR EXP_EXPEDIENTES */
                SELECT COUNT(*) INTO NCOUNT FROM EXP_EXPEDIENTES WHERE EXP_ID = rEXP.EXP_ID;

                SELECT DD_EST_ID INTO vDD_EST_ID FROM ${master.schema}.DD_EST_ESTADOS_ITINERARIOS WHERE DD_EST_CODIGO = 'VMV';

                IF ncount <= 0 THEN
                  SELECT DD_TPX_ID INTO vDD_TPX_ID FROM DD_TPX_TIPO_EXPEDIENTE WHERE DD_TPX_CODIGO = 'REC';

                  EXECUTE IMMEDIATE 'INSERT INTO EXP_EXPEDIENTES (EXP_ID, EXP_DESCRIPCION, DD_EST_ID, EXP_FECHA_EST_ID, OFI_ID, ARQ_ID, DD_EEX_ID, EXP_MANUAL, DD_TPX_ID, VERSION, USUARIOCREAR, FECHACREAR, BORRADO)
                                  VALUES(
                                  :EXP_ID, :EXP_DESCRIPCION, :DD_EST_ID, SYSDATE, :OFI_ID, :ARQ_ID, :EEX_ID , :EXP_MANUAL, :TPX, 0, :USUARIOCREAR, SYSDATE, 0
                                )'
                  USING rEXP.EXP_ID, rEXP.EXP_DESCRIPCION, vDD_EST_ID, rEXP.OFI_ID, cARQ_GENERICO, cEEX_ID, rEXP.EXP_MANUAL, vDD_TPX_ID, cUSUARIO_BATCH;

                  /* INICIO INSERTAR EXR_EXPEDIENTE_RECOBRO */
                  EXECUTE IMMEDIATE 'INSERT INTO EXR_EXPEDIENTE_RECOBRO (EXP_ID) VALUES(:EXP_ID)'
                  USING rEXP.EXP_ID;
                  /* FIN INSERTAR EXR_EXPEDIENTE_RECOBRO */

                END IF;
                /* FIN INSERTAR EXP_EXPEDIENTES */



                /* INICIO BUCLE BATCH_DATOS_SALIDA CEX*/
                IF NOT curDATOS_CEX%ISOPEN THEN
                    OPEN curDATOS_CEX(rEXP.EXP_ID);
                END IF;
                LOOP
                  FETCH curDATOS_CEX INTO rDATOS_CEX;
                  EXIT WHEN curDATOS_CEX%NOTFOUND;

                  /* INICIO INSERTAR CEX */
                  SELECT S_CEX_CONTRATOS_EXPEDIENTE.NEXTVAL INTO vCEX_ID FROM DUAL;

                  EXECUTE IMMEDIATE 'SELECT DD_AEX_ID FROM '||cMASTER_SCHEMA||'.DD_AEX_AMBITOS_EXPEDIENTE WHERE DD_AEX_CODIGO=:AEX_CODIGO'
                  INTO vAEX_ID
                  USING rDATOS_CEX.DD_AEX_CODIGO;

                  EXECUTE IMMEDIATE 'INSERT INTO CEX_CONTRATOS_EXPEDIENTE (CEX_ID, CNT_ID, EXP_ID, DD_AEX_ID, CEX_PASE, VERSION, USUARIOCREAR,FECHACREAR, BORRADO)
                                      VALUES (:CEX_ID, :CNT_ID, :EXP_ID, :AEX_ID, :CEX_PASE, 0, :USUARIOCREAR, SYSDATE, 0)'
                  USING vCEX_ID, rDATOS_CEX.CNT_ID, rDATOS_CEX.EXP_ID, vAEX_ID, rDATOS_CEX.CEX_PASE, cUSUARIO_BATCH;
                  /* FIN INSERTAR CEX */

                END LOOP;
                /* FIN BUCLE BATCH_DATOS_SALIDA CEX*/
                CLOSE curDATOS_CEX;


                /* INICIO BUCLE BATCH_DATOS_SALIDA PEX*/
                IF NOT curDATOS_PEX%ISOPEN THEN
                    OPEN curDATOS_PEX(rEXP.EXP_ID);
                END IF;
                LOOP
                  FETCH curDATOS_PEX INTO rDATOS_PEX;
                  EXIT WHEN curDATOS_PEX%NOTFOUND;

                  /* INICIO INSERTAR PEX */
                  SELECT S_PEX_PERSONAS_EXPEDIENTE.NEXTVAL INTO vPEX_ID FROM DUAL;

                  EXECUTE IMMEDIATE 'INSERT INTO PEX_PERSONAS_EXPEDIENTE (PEX_ID, EXP_ID, PER_ID, DD_AEX_ID, PEX_PASE, VERSION, USUARIOCREAR, FECHACREAR, BORRADO)
                                      VALUES
                                      (:PEX_ID, :EXP_ID, :PER_ID, :AEX_ID, :PEX_PASE, 0, :USUARIOCREAR, SYSDATE, 0)'
                  USING vPEX_ID, rDATOS_PEX.EXP_ID, rDATOS_PEX.PER_ID, vAEX_ID, rDATOS_PEX.PEX_PASE, cUSUARIO_BATCH;
                  /* INICIO INSERTAR PEX */

                END LOOP;
                /* FIN BUCLE BATCH_DATOS_SALIDA */
                CLOSE curDATOS_PEX;

              END LOOP;
              /* FIN BUCLE NUEVOS EXPEDIENTES */
              CLOSE curEXP_NUEVOS;

              COMMIT;

              OPERACION_DDL.DDL_TABLE('STATS','EXP_EXPEDIENTES');
              OPERACION_DDL.DDL_TABLE('STATS','EXR_EXPEDIENTE_RECOBRO');
              OPERACION_DDL.DDL_TABLE('STATS','CEX_CONTRATOS_EXPEDIENTE');
              OPERACION_DDL.DDL_TABLE('STATS','PEX_PERSONAS_EXPEDIENTE');
        end;

/* * ABRIR NUEVOS CICLOS DE RECOBRO SI HACE FALTA */ declare
vDD_TGC_ID NUMBER(16); V_TABLE NUMBER;
begin
select DD_TGC_ID into vDD_TGC_ID from RCF_DD_TGC_TIPO_GESTION_CART where DD_TGC_CODIGO = 'GI';
DELETE FROM TMP_CNT_PER_EXCEPTUADOS; COMMIT;
--** Cogemos, para descartar de la búsqueda: -- a.Personas exceptuadas. -- b.Contratos exceptuados. -- c.Personas con contratos exceptuados que no tengan también contratos no exceptuados. -- d.Contratos de personas exceptuadas que no sean también de personas no exceptuadas. -- INSERT INTO TMP_CNT_PER_EXCEPTUADOS SELECT /*+ FIRST_ROWS */ exc1.exc_id , exc1.per_id as per_id , nvl(dcp2.cnt_id,0) as cnt_id FROM -- Personas exceptuadas BATCH_DATOS_EXCEPTUADOS exc1 -- Contratos de personas exceptuadas , (Select dcp1.per_id, dcp1.cnt_id From BATCH_DATOS_CNT_PER dcp1 -- Contratos de personas no exceptuadas , (Select a.cnt_id From BATCH_DATOS_CNT_PER a, BATCH_DATOS_EXCEPTUADOS b Where a.per_id = b.per_id(+) and b.exc_id is null) exc2 Where dcp1.cnt_id = exc2.cnt_id(+) and exc2.cnt_id is null ) dcp2 WHERE exc1.per_id = dcp2.per_id(+) AND exc1.per_id is not null UNION SELECT exc1.exc_id , nvl(dcp2.per_id,0) as per_id , exc1.cnt_id as cnt_id FROM -- Contratos exceptuados BATCH_DATOS_EXCEPTUADOS exc1 -- Personas con contratos exceptuados , (Select dcp1.cnt_id, dcp1.per_id From BATCH_DATOS_CNT_PER dcp1 -- Personas sin contratos exceptuados , (Select a.per_id From BATCH_DATOS_CNT_PER a, BATCH_DATOS_EXCEPTUADOS b Where a.cnt_id = b.cnt_id(+) and b.exc_id is null) exc2 Where dcp1.per_id = exc2.per_id(+) and exc2.per_id is null ) dcp2 WHERE exc1.cnt_id = dcp2.cnt_id(+) AND exc1.cnt_id is not null;
OPERACION_DDL.DDL_TABLE('STATS','TMP_CNT_PER_EXCEPTUADOS',50);
INSERT INTO TMP_CICLOS_RECOBRO_PTES SELECT EXP_ID, RCF_SCA_ID, RCF_AGE_ID, NCRE, NCRC, NCRP FROM (SELECT DISTINCT REP.EXP_ID, REP.RCF_SCA_ID, REP.RCF_AGE_ID -- contamos los ciclos de deuda del expediente creados , COUNT(DISTINCT CRE.CRE_ID) NCRE -- contamos la diferecia entre los contratos y los cilcos de recobro de contratos abiertas , (COUNT(DISTINCT CEX.CNT_ID) - COUNT(DISTINCT CRC.CNT_ID)) NCRC -- contamos la diferencia entre las personas y los ciclos de recobro de personas abiertos , (COUNT(DISTINCT PEX.PER_ID) - COUNT(DISTINCT CRP.PER_ID)) NCRP FROM (-- Repartidas SELECT EXP_ID, RCF_SCA_ID, RCF_AGE_ID FROM TMP_REC_EXP_REPARTO_AGENCIAS UNION SELECT EXP_ID, RCF_SCA_ID, RCF_AGE_ID FROM TMP_REC_EXP_AGE_CNT_EXC TMP GROUP BY EXP_ID, RCF_SCA_ID, RCF_AGE_ID ) REP JOIN EXR_EXPEDIENTE_RECOBRO EXP ON REP.EXP_ID = EXP.EXP_ID -- nos aseguramos que los deben gestionarse JOIN (SELECT DISTINCT EXP_ID FROM (SELECT EXP_ID FROM BATCH_DATOS_EXP UNION SELECT EXP_ID FROM BATCH_DATOS_SALIDA ) ) REX ON EXP.EXP_ID = REX.EXP_ID JOIN CEX_CONTRATOS_EXPEDIENTE CEX ON EXP.EXP_ID = CEX.EXP_ID AND CEX.BORRADO = 0 -- Controlamos que el contrato no esté exceptuado LEFT JOIN TMP_CNT_PER_EXCEPTUADOS EXCC ON CEX.CNT_ID = EXCC.CNT_ID -- Controlamos que el contrato esté activo y aprovisionado JOIN BATCH_DATOS_CNT CNT ON CEX.CNT_ID = CNT.CNT_ID JOIN PEX_PERSONAS_EXPEDIENTE PEX ON EXP.EXP_ID = PEX.EXP_ID AND PEX.BORRADO = 0 -- Controlamos que la persona no esté exceptuada LEFT JOIN TMP_CNT_PER_EXCEPTUADOS EXCP ON PEX.PER_ID = EXCP.PER_ID -- Controlamos que la persona esté activa y aprovisionada JOIN BATCH_DATOS_PER PER ON PEX.PER_ID = PER.PER_ID LEFT JOIN CRE_CICLO_RECOBRO_EXP CRE ON EXP.EXP_ID = CRE.EXP_ID AND CRE.CRE_FECHA_BAJA IS NULL AND CRE.BORRADO = 0 LEFT JOIN CRC_CICLO_RECOBRO_CNT CRC ON CRE.CRE_ID = CRC.CRE_ID AND CRC.CRC_FECHA_BAJA IS NULL AND CRC.BORRADO = 0 LEFT JOIN CRP_CICLO_RECOBRO_PER CRP ON CRE.CRE_ID = CRP.CRE_ID AND CRP.CRP_FECHA_BAJA IS NULL AND CRP.BORRADO = 0 WHERE EXCC.EXC_ID IS NULL AND EXCP.EXC_ID IS NULL GROUP BY REP.EXP_ID, REP.RCF_SCA_ID, REP.RCF_AGE_ID ) WHERE -- le falte el ciclo del expediente NCRE = 0 -- haya algún contrato sin ciclo OR NCRC > 0 -- haya alguna persona sin ciclo OR NCRP > 0;
EXECUTE IMMEDIATE 'SELECT COUNT(1) FROM ALL_INDEXES WHERE INDEX_NAME=''IDX_TMP_CICLOS_RECOBRO_PTES_1''' INTO V_TABLE; IF V_TABLE = 0 THEN OPERACION_DDL.DDL_INDEX('CREATE','IDX_TMP_CICLOS_RECOBRO_PTES_1','TMP_CICLOS_RECOBRO_PTES(EXP_ID)','N'); END IF; EXECUTE IMMEDIATE 'SELECT COUNT(1) FROM ALL_INDEXES WHERE INDEX_NAME=''IDX_TMP_CICLOS_RECOBRO_PTES_2''' INTO V_TABLE; IF V_TABLE = 0 THEN OPERACION_DDL.DDL_INDEX('CREATE','IDX_TMP_CICLOS_RECOBRO_PTES_2','TMP_CICLOS_RECOBRO_PTES(NCRE)','N'); END IF; EXECUTE IMMEDIATE 'SELECT COUNT(1) FROM ALL_INDEXES WHERE INDEX_NAME=''IDX_TMP_CICLOS_RECOBRO_PTES_3''' INTO V_TABLE; IF V_TABLE = 0 THEN OPERACION_DDL.DDL_INDEX('CREATE','IDX_TMP_CICLOS_RECOBRO_PTES_3','TMP_CICLOS_RECOBRO_PTES(NCRC)','N'); END IF; EXECUTE IMMEDIATE 'SELECT COUNT(1) FROM ALL_INDEXES WHERE INDEX_NAME=''IDX_TMP_CICLOS_RECOBRO_PTES_4''' INTO V_TABLE; IF V_TABLE = 0 THEN OPERACION_DDL.DDL_INDEX('CREATE','IDX_TMP_CICLOS_RECOBRO_PTES_4','TMP_CICLOS_RECOBRO_PTES(NCRP)','N'); END IF;
OPERACION_DDL.DDL_TABLE('STATS','TMP_CICLOS_RECOBRO_PTES');
INSERT INTO TMP_CRE_NUEVOS SELECT TMP.EXP_ID , ESC.RCF_ESQ_ID , ESC.RCF_ESC_ID , SCA.RCF_SCA_ID , SUA.RCF_SUA_ID , SUA.RCF_AGE_ID , SUM(NVL(MOV.MOV_POS_VIVA_NO_VENCIDA,0)) AS CRE_POS_VIVA_NO_VENCIDA , SUM(NVL(MOV.MOV_POS_VIVA_VENCIDA,0)) AS CRE_POS_VIVA_VENCIDA , SUM(NVL(MOV.MOV_INT_REMUNERATORIOS,0)) AS CRE_INT_ORDIN_DEVEN , SUM(NVL(MOV.MOV_INT_MORATORIOS,0)) AS CRE_INT_MORAT_DEVEN , SUM(NVL(MOV.MOV_COMISIONES,0)) AS CRE_COMISIONES , SUM(NVL(MOV.MOV_GASTOS,0)) AS CRE_GASTOS , SUM(NVL(MOV.MOV_IMPUESTOS,0)) AS CRE_IMPUESTOS FROM TMP_CICLOS_RECOBRO_PTES TMP JOIN RCF_SCA_SUBCARTERA SCA ON TMP.RCF_SCA_ID = SCA.RCF_SCA_ID AND SCA.BORRADO = 0 JOIN RCF_ESC_ESQUEMA_CARTERAS ESC ON SCA.RCF_ESC_ID = ESC.RCF_ESC_ID AND ESC.BORRADO = 0 JOIN RCF_SUA_SUBCARTERA_AGENCIAS SUA ON TMP.RCF_AGE_ID = SUA.RCF_AGE_ID AND SCA.RCF_SCA_ID = SUA.RCF_SCA_ID AND SUA.BORRADO = 0 JOIN CEX_CONTRATOS_EXPEDIENTE CEX ON TMP.EXP_ID = CEX.EXP_ID AND CEX.BORRADO = 0 JOIN CNT_CONTRATOS CNT ON CEX.CNT_ID = CNT.CNT_ID AND CNT.BORRADO = 0 JOIN MOV_MOVIMIENTOS MOV ON CNT.CNT_ID = MOV.CNT_ID AND CNT.CNT_FECHA_EXTRACCION = MOV.MOV_FECHA_EXTRACCION WHERE TMP.NCRE = 0 GROUP BY TMP.EXP_ID , ESC.RCF_ESQ_ID , ESC.RCF_ESC_ID , SCA.RCF_SCA_ID , SUA.RCF_SUA_ID , SUA.RCF_AGE_ID;
INSERT INTO CRE_CICLO_RECOBRO_EXP (CRE_ID, EXP_ID, CRE_FECHA_ALTA, RCF_ESQ_ID, RCF_ESC_ID, RCF_SCA_ID, RCF_SUA_ID, RCF_AGE_ID , CRE_POS_VIVA_NO_VENCIDA, CRE_POS_VIVA_VENCIDA, CRE_INT_ORDIN_DEVEN, CRE_INT_MORAT_DEVEN , CRE_COMISIONES, CRE_GASTOS, CRE_IMPUESTOS, DD_TGC_ID, RCF_MFA_ID, VERSION, USUARIOCREAR, FECHACREAR, BORRADO) SELECT S_CRE_CICLO_RECOBRO_EXP.NEXTVAL , TMP.EXP_ID , TRUNC(SYSDATE) , TMP.RCF_ESQ_ID , TMP.RCF_ESC_ID , TMP.RCF_SCA_ID , TMP.RCF_SUA_ID , TMP.RCF_AGE_ID , TMP.CRE_POS_VIVA_NO_VENCIDA , TMP.CRE_POS_VIVA_VENCIDA , TMP.CRE_INT_ORDIN_DEVEN , TMP.CRE_INT_MORAT_DEVEN , TMP.CRE_COMISIONES , TMP.CRE_GASTOS , TMP.CRE_IMPUESTOS , vDD_TGC_ID , SCA.RCF_MFA_ID , 0, '${usuario.crear}', SYSDATE,0 FROM TMP_CRE_NUEVOS TMP JOIN RCF_SCA_SUBCARTERA SCA ON TMP.RCF_SCA_ID = SCA.RCF_SCA_ID;
-- OPERACION_DDL.DDL_TABLE('STATS','CRE_CICLO_RECOBRO_EXP');
INSERT INTO TMP_CRC_NUEVOS SELECT DISTINCT CEX.CNT_ID , CRE.CRE_ID , to_number(to_char(TRUNC(sysdate), 'yyyyMMdd') || CEX.CNT_ID) AS CRC_ID_ENVIO , TRUNC(SYSDATE) AS CRC_FECHA_ALTA , NVL(MOV.MOV_POS_VIVA_NO_VENCIDA,0) AS CRC_POS_VIVA_NO_VENCIDA , NVL(MOV.MOV_POS_VIVA_VENCIDA,0) AS CRC_POS_VIVA_VENCIDA , NVL(MOV.MOV_INT_REMUNERATORIOS,0) AS CRC_INT_ORDIN_DEVEN , NVL(MOV.MOV_INT_MORATORIOS,0) AS CRC_INT_MORAT_DEVEN , NVL(MOV.MOV_COMISIONES,0) AS CRC_COMISIONES , NVL(MOV.MOV_GASTOS,0) AS CRC_GASTOS , NVL(MOV.MOV_IMPUESTOS,0) AS CRC_IMPUESTOS FROM TMP_CICLOS_RECOBRO_PTES TMP JOIN CEX_CONTRATOS_EXPEDIENTE CEX ON TMP.EXP_ID = CEX.EXP_ID AND CEX.BORRADO = 0 JOIN BATCH_DATOS_CNT CNTB ON CEX.CNT_ID = CNTB.CNT_ID -- no estén exceptuados LEFT JOIN TMP_CNT_PER_EXCEPTUADOS EXC ON CNTB.CNT_ID = EXC.CNT_ID JOIN CRE_CICLO_RECOBRO_EXP CRE ON CEX.EXP_ID = CRE.EXP_ID AND CRE.CRE_FECHA_BAJA IS NULL JOIN RCF_ESC_ESQUEMA_CARTERAS ESC ON CRE.RCF_ESC_ID = ESC.RCF_ESC_ID JOIN RCF_DD_TGC_TIPO_GESTION_CART TGC ON ESC.DD_TGC_ID = TGC.DD_TGC_ID JOIN CNT_CONTRATOS CNT ON CEX.CNT_ID = CNT.CNT_ID JOIN MOV_MOVIMIENTOS MOV ON CNT.CNT_ID = MOV.CNT_ID AND CNT.CNT_FECHA_EXTRACCION = MOV.MOV_FECHA_EXTRACCION -- cruzamos con los ciclos de contrato para no volver a abirlos LEFT JOIN CRC_CICLO_RECOBRO_CNT OTR ON OTR.BORRADO = 0 AND CRE.CRE_ID = OTR.CRE_ID AND OTR.CNT_ID = CEX.CNT_ID AND OTR.CRC_FECHA_BAJA IS NULL WHERE TMP.NCRC > 0 AND (TGC.DD_TGC_CODIGO = 'GC' OR CNTB.CNT_RIESGO > 0) -- comprobamos que no haya ciclos de contrato abiertos AND OTR.CRC_ID IS NULL -- ni sea contrato exceptuado AND EXC.EXC_ID IS NULL;
INSERT INTO CRC_CICLO_RECOBRO_CNT (CRC_ID, CNT_ID, CRE_ID, CRC_ID_ENVIO, CRC_FECHA_ALTA , CRC_POS_VIVA_NO_VENCIDA, CRC_POS_VIVA_VENCIDA, CRC_INT_ORDIN_DEVEN, CRC_INT_MORAT_DEVEN, CRC_COMISIONES, CRC_GASTOS, CRC_IMPUESTOS , VERSION, USUARIOCREAR, FECHACREAR, BORRADO) SELECT S_CRC_CICLO_RECOBRO_CNT.NEXTVAL , CNT_ID , CRE_ID , CRC_ID_ENVIO , TRUNC(SYSDATE) AS CRC_FECHA_ALTA , CRC_POS_VIVA_NO_VENCIDA , CRC_POS_VIVA_VENCIDA , CRC_INT_ORDIN_DEVEN , CRC_INT_MORAT_DEVEN , CRC_COMISIONES , CRC_GASTOS , CRC_IMPUESTOS , 0 VERSION, '${usuario.crear}' USUARIOCREAR, SYSDATE FECHACREAR,0 BORRADO FROM TMP_CRC_NUEVOS;
-- OPERACION_DDL.DDL_TABLE('STATS','CRC_CICLO_RECOBRO_CNT');
INSERT INTO TMP_CRP_NUEVOS SELECT DISTINCT PEX.PER_ID , CRE.CRE_ID , TRUNC(SYSDATE) AS CRP_FECHA_ALTA , PERB.PER_RIESGO_DIRECTO CRP_RIESGO_DIRECTO , PERB.PER_RIESGO_INDIRECTO CRP_RIESGO_INDIRECTO FROM TMP_CICLOS_RECOBRO_PTES TMP JOIN PEX_PERSONAS_EXPEDIENTE PEX ON TMP.EXP_ID = PEX.EXP_ID AND PEX.BORRADO = 0 JOIN BATCH_DATOS_PER PERB ON PEX.PER_ID = PERB.PER_ID LEFT JOIN TMP_CNT_PER_EXCEPTUADOS EXCP ON PEX.PER_ID = EXCP.PER_ID JOIN CRE_CICLO_RECOBRO_EXP CRE ON PEX.EXP_ID = CRE.EXP_ID AND CRE.CRE_FECHA_BAJA IS NULL JOIN RCF_ESC_ESQUEMA_CARTERAS ESC ON CRE.RCF_ESC_ID = ESC.RCF_ESC_ID JOIN RCF_DD_TGC_TIPO_GESTION_CART TGC ON ESC.DD_TGC_ID = TGC.DD_TGC_ID JOIN CPE_CONTRATOS_PERSONAS CPE ON PEX.PER_ID = CPE.PER_ID AND CPE.BORRADO = 0 JOIN CEX_CONTRATOS_EXPEDIENTE CEX ON CPE.CNT_ID = CEX.CNT_ID AND CEX.EXP_ID = PEX.EXP_ID AND CEX.BORRADO = 0 JOIN BATCH_DATOS_CNT CNTB ON CEX.CNT_ID = CNTB.CNT_ID LEFT JOIN TMP_CNT_PER_EXCEPTUADOS EXCC ON CNTB.CNT_ID = EXCC.CNT_ID -- cruzamos con los ciclos de la persona para no volver a abrirlos LEFT JOIN CRP_CICLO_RECOBRO_PER OTR ON OTR.BORRADO = 0 AND CRE.CRE_ID = OTR.CRE_ID AND OTR.PER_ID = PEX.PER_ID AND OTR.CRP_FECHA_BAJA IS NULL WHERE TMP.NCRP > 0 AND (TGC.DD_TGC_CODIGO = 'GC' OR CNTB.CNT_RIESGO > 0) -- comprobamos exceptuados AND EXCC.EXC_ID IS NULL AND EXCP.EXC_ID IS NULL -- comprobamos que ho haya ciclos de persona abiertos AND OTR.PER_ID IS NULL;
INSERT INTO CRP_CICLO_RECOBRO_PER (CRP_ID, PER_ID, CRE_ID, CRP_FECHA_ALTA, CRP_RIESGO_DIRECTO, CRP_RIESGO_INDIRECTO , VERSION, USUARIOCREAR, FECHACREAR, BORRADO) SELECT S_CRP_CICLO_RECOBRO_PER.NEXTVAL , PER_ID , CRE_ID , CRP_FECHA_ALTA , CRP_RIESGO_DIRECTO , CRP_RIESGO_INDIRECTO , 0 VERSION, '${usuario.crear}' USUARIOCREAR, SYSDATE FECHACREAR,0 BORRADO FROM TMP_CRP_NUEVOS;
-- OPERACION_DDL.DDL_TABLE('STATS','CRP_CICLO_RECOBRO_PER');
COMMIT;
end;

        
        BEGIN
            EXECUTE IMMEDIATE '
                INSERT INTO TMP_GAE_GAGER
                (SELECT GAE.EXP_ID FROM GAE_GESTOR_ADD_EXPEDIENTE GAE
                   INNER JOIN GEE_GESTOR_ENTIDAD GEE ON GEE.BORRADO = ${borrado.no.id} AND GAE.GEE_ID = GEE.GEE_ID
                    INNER JOIN ${master.schema}.DD_TGE_TIPO_GESTOR TGE ON TGE.BORRADO = ${borrado.no.id} AND GEE.DD_TGE_ID = TGE.DD_TGE_ID AND TGE.DD_TGE_CODIGO = '${ddTipoGestor.Recobro.codigo}')
            ';
            /*EXECUTE IMMEDIATE 'CREATE INDEX IDX_TMP_GAE_GEE_ID ON TMP_GAE_GAGER(GEE_ID)';*/
            OPERACION_DDL.DDL_INDEX('CREATE','IDX_TMP_GAE_GEE_ID'); --rebuild
            OPERACION_DDL.DDL_TABLE('STATS','TMP_GAE_GAGER');

        END;

declare -- Cursor de expedientes sin gestor de recobro en ciclo de recobro CURSOR C_EXP_SIN_GESTOR_RECOBRO IS SELECT EXP.EXP_ID, AGE.USU_ID, S_GEE_GESTOR_ENTIDAD.NEXTVAL NUEVO_GEE_ID, S_GEH_GESTOR_ENTIDAD_HIST.NEXTVAL NUEVO_GEH_ID FROM EXP_EXPEDIENTES EXP INNER JOIN CRE_CICLO_RECOBRO_EXP CRE ON CRE.BORRADO = 0 AND EXP.EXP_ID = CRE.EXP_ID AND CRE.CRE_FECHA_BAJA IS NULL INNER JOIN ${master.schema}.DD_EEX_ESTADO_EXPEDIENTE DD ON DD.BORRADO = 0 AND EXP.DD_EEX_ID = DD.DD_EEX_ID AND DD.DD_EEX_CODIGO = '1' INNER JOIN RCF_AGE_AGENCIAS AGE ON CRE.RCF_AGE_ID = AGE.RCF_AGE_ID AND AGE.BORRADO = 0 WHERE EXP.BORRADO = 0 AND EXP.EXP_MANUAL = 0 -- QUE NO TENGAN UN GAE DE TIPO RECOBRO AND EXP.EXP_ID NOT IN (SELECT GEE_ID FROM TMP_GAE_GAGER);
TYPE T_EXP_SIN_G IS TABLE OF C_EXP_SIN_GESTOR_RECOBRO%ROWTYPE INDEX BY BINARY_INTEGER; L_EXP_SIN_G T_EXP_SIN_G;
-- CURSOR DE EXPEDIENTES QUE TENGAN UN GESTOR DE RECOBRO DISTINTO AL DE LA AGENCIA REPARTIDA CURSOR C_EXP_CAMBIAR_USU IS SELECT EXP.EXP_ID, GEE.USU_ID, AGE.USU_ID USU_AGE, S_GEE_GESTOR_ENTIDAD.NEXTVAL NUEVO_GEE_ID, S_GEH_GESTOR_ENTIDAD_HIST.NEXTVAL NUEVO_GEH_ID FROM EXP_EXPEDIENTES EXP INNER JOIN CRE_CICLO_RECOBRO_EXP CRE ON CRE.BORRADO = 0 AND EXP.EXP_ID = CRE.EXP_ID AND CRE.CRE_FECHA_BAJA IS NULL INNER JOIN ${master.schema}.DD_EEX_ESTADO_EXPEDIENTE DD ON DD.BORRADO = 0 AND EXP.DD_EEX_ID = DD.DD_EEX_ID AND DD.DD_EEX_CODIGO = '1' INNER JOIN GAE_GESTOR_ADD_EXPEDIENTE GAE ON EXP.EXP_ID = GAE.EXP_ID INNER JOIN GEE_GESTOR_ENTIDAD GEE ON GEE.BORRADO = 0 AND GAE.GEE_ID = GEE.GEE_ID INNER JOIN ${master.schema}.DD_TGE_TIPO_GESTOR TGE ON TGE.BORRADO = 0 AND GEE.DD_TGE_ID = TGE.DD_TGE_ID AND TGE.DD_TGE_CODIGO = 'GAGER' INNER JOIN RCF_AGE_AGENCIAS AGE ON AGE.BORRADO = 0 AND CRE.RCF_AGE_ID = AGE.RCF_AGE_ID WHERE EXP.BORRADO = 0 AND EXP.EXP_MANUAL = 0 AND GEE.USU_ID <> AGE.USU_ID;
TYPE T_EXP_CAMB_G IS TABLE OF C_EXP_CAMBIAR_USU%ROWTYPE INDEX BY BINARY_INTEGER; L_EXP_CAMB_G T_EXP_CAMB_G;
V_DD_TGE_ID NUMBER(16,0);
BEGIN OPEN C_EXP_SIN_GESTOR_RECOBRO;
-- OBTENEMOS EL ID DE TIPO GERECOBRO SELECT TGE.DD_TGE_ID INTO V_DD_TGE_ID FROM ${master.schema}.DD_TGE_TIPO_GESTOR TGE WHERE TGE.BORRADO = 0 AND TGE.DD_TGE_CODIGO = 'GAGER';
LOOP -- EXPEDIENTES SIN GESTOR FETCH C_EXP_SIN_GESTOR_RECOBRO BULK COLLECT INTO L_EXP_SIN_G LIMIT 1000;
-- INSERTAMOS UN REGISTRO EN GEE_GESTOR_ENTIDAD FORALL I IN 1..L_EXP_SIN_G.COUNT INSERT INTO GEE_GESTOR_ENTIDAD (GEE_ID, USU_ID, DD_TGE_ID, VERSION, USUARIOCREAR, FECHACREAR, BORRADO) VALUES(L_EXP_SIN_G(I).NUEVO_GEE_ID, L_EXP_SIN_G(I).USU_ID, V_DD_TGE_ID, 0, '${usuario.crear}', SYSDATE, 0);
-- INSERTAMOS UN REGISTRO EN GAE_GESTOR_ADD_EXPEDIENTE FORALL I IN 1..L_EXP_SIN_G.COUNT INSERT INTO GAE_GESTOR_ADD_EXPEDIENTE (GEE_ID, EXP_ID) VALUES (L_EXP_SIN_G(I).NUEVO_GEE_ID, L_EXP_SIN_G(I).EXP_ID);
-- AHORA INSERTAMOS SUS CORRESPONDIENTES REGISTROS DE HISTORICO FORALL I IN 1..L_EXP_SIN_G.COUNT INSERT INTO GEH_GESTOR_ENTIDAD_HIST (GEH_ID, USU_ID, DD_TGE_ID, GEH_FECHA_DESDE, GEH_FECHA_HASTA, VERSION, USUARIOCREAR, FECHACREAR, BORRADO) VALUES (L_EXP_SIN_G(I).NUEVO_GEH_ID, L_EXP_SIN_G(I).USU_ID, V_DD_TGE_ID, TRUNC(SYSDATE), NULL, 0, '${usuario.crear}', SYSDATE, 0);
FORALL I IN 1..L_EXP_SIN_G.COUNT INSERT INTO GEH_GESTOR_EXPEDIENTE_HIST (GEH_ID, EXP_ID) VALUES (L_EXP_SIN_G(I).NUEVO_GEH_ID, L_EXP_SIN_G(I).EXP_ID);
EXIT WHEN C_EXP_SIN_GESTOR_RECOBRO%NOTFOUND; END LOOP; -- LOOP EXP_SIN_GESTOR_RECOBRO CLOSE C_EXP_SIN_GESTOR_RECOBRO;
OPEN C_EXP_CAMBIAR_USU; LOOP -- EXPEDIENTES QUE HAY QUE CAMBIAR EL GESTOR DE RECOBRO FETCH C_EXP_CAMBIAR_USU BULK COLLECT INTO L_EXP_CAMB_G LIMIT 1000;
-- CANCELAMOS EL HISTORICO DE GEH_GESTORI_ENTIDAD_HIST FORALL I IN 1..L_EXP_CAMB_G.COUNT UPDATE GEH_GESTOR_ENTIDAD_HIST SET GEH_FECHA_HASTA = TRUNC(SYSDATE), USUARIOMODIFICAR = '${usuario.crear}', FECHAMODIFICAR = SYSDATE WHERE GEH_ID IN (SELECT GEH_ID FROM GEH_GESTOR_EXPEDIENTE_HIST WHERE EXP_ID = L_EXP_CAMB_G(I).EXP_ID) --AND USU_ID = V_GEE_USU_ID AND DD_TGE_ID = V_DD_TGE_ID AND GEH_FECHA_HASTA IS NULL AND BORRADO = 0;
-- MARCAMOS COMO BORRADO EL ANTIGUO REGISTRO DE GEE_GESTOR_ENTIDAD FORALL I IN 1..L_EXP_CAMB_G.COUNT UPDATE GEE_GESTOR_ENTIDAD SET BORRADO = 1, USUARIOBORRAR = '${usuario.crear}', FECHABORRAR = SYSDATE WHERE GEE_ID IN (SELECT GEE_ID FROM GAE_GESTOR_ADD_EXPEDIENTE GAE WHERE GAE.EXP_ID = L_EXP_CAMB_G(I).EXP_ID) --AND USU_ID = V_GEE_USU_ID AND DD_TGE_ID = V_DD_TGE_ID AND BORRADO = 0;
-- INSERTAMOS UN REGISTRO EN GEE_GESTOR_ENTIDAD FORALL I IN 1..L_EXP_CAMB_G.COUNT INSERT INTO GEE_GESTOR_ENTIDAD (GEE_ID, USU_ID, DD_TGE_ID, VERSION, USUARIOCREAR, FECHACREAR, BORRADO) VALUES(L_EXP_CAMB_G(I).NUEVO_GEE_ID, L_EXP_CAMB_G(I).USU_AGE, V_DD_TGE_ID, 0, '${usuario.crear}', SYSDATE, 0);
-- INSERTAMOS UN REGISTRO EN GAE_GESTOR_ADD_EXPEDIENTE FORALL I IN 1..L_EXP_CAMB_G.COUNT INSERT INTO GAE_GESTOR_ADD_EXPEDIENTE (GEE_ID, EXP_ID) VALUES (L_EXP_CAMB_G(I).NUEVO_GEE_ID, L_EXP_CAMB_G(I).EXP_ID);
-- AHORA INSERTAMOS SUS CORRESPONDIENTES REGISTROS DE HISTORICO FORALL I IN 1..L_EXP_CAMB_G.COUNT INSERT INTO GEH_GESTOR_ENTIDAD_HIST (GEH_ID, USU_ID, DD_TGE_ID, GEH_FECHA_DESDE, GEH_FECHA_HASTA, VERSION, USUARIOCREAR, FECHACREAR, BORRADO) VALUES (L_EXP_CAMB_G(I).NUEVO_GEH_ID, L_EXP_CAMB_G(I).USU_AGE, V_DD_TGE_ID, TRUNC(SYSDATE), NULL, 0, '${usuario.crear}', SYSDATE, 0);
FORALL I IN 1..L_EXP_CAMB_G.COUNT INSERT INTO GEH_GESTOR_EXPEDIENTE_HIST (GEH_ID, EXP_ID) VALUES (L_EXP_CAMB_G(I).NUEVO_GEH_ID, L_EXP_CAMB_G(I).EXP_ID);
-- MARCAMOS COMO BORRADO SU HISTORICO PARA QUE NO APAREZCA EN EL MENU DE FAVORITOS FORALL I IN 1..L_EXP_CAMB_G.COUNT UPDATE HAC_HISTORICO_ACCESOS SET BORRADO =1, USUARIOBORRAR = 'REC-BATCH', FECHABORRAR = sysdate WHERE EXP_ID = L_EXP_CAMB_G(I).EXP_ID AND USU_ID = L_EXP_CAMB_G(I).USU_ID;
EXIT WHEN C_EXP_CAMBIAR_USU%NOTFOUND; END LOOP; -- LOOP EXP_CAMBIAR_USU
CLOSE C_EXP_CAMBIAR_USU; END;

        
            begin
              OPERACION_DDL.DDL_TABLE('TRUNCATE','REC_FICHERO_CONTRATOS');
            end;


    BEGIN
        OPERACION_DDL.DDL_INDEX('CREATE','IDX_TMP_SAGER_INFO_1'); --rebuild
        OPERACION_DDL.DDL_TABLE('STATS','TMP_SAGER_INFO');
    END;


    begin
      OPERACION_DDL.DDL_TABLE('TRUNCATE','REC_FICHERO_PERSONAS');
    end;


    begin
      OPERACION_DDL.DDL_TABLE('TRUNCATE','REC_FICHERO_CONTRATOS_PERSONAS');
    end;

        
        DECLARE
            /*
          * CONSTANTES
          */
          C_MASTER_SCHEMA VARCHAR2(50 CHAR);

          /* CURSOSRS */
          /*
           * Este cursor sirve para detectar los contratos en REC_FICHERO que no tienen relaciones
           */
         CURSOR C_SIN_REL IS
          SELECT DISTINCT REC.RCF_AGE_ID,REC.ID_ENVIO,CRC.CNT_ID
          FROM REC_FICHERO_CONTRATOS REC
            JOIN CRC_CICLO_RECOBRO_CNT CRC ON REC.ID_ENVIO = CRC.CRC_ID_ENVIO
          WHERE NOT EXISTS
            (SELECT 1
            FROM REC_FICHERO_CONTRATOS_PERSONAS
            WHERE RCF_AGE_ID = REC.RCF_AGE_ID
            AND ID_ENVIO     = REC.ID_ENVIO
            );

          TYPE T_CNT IS TABLE OF C_SIN_REL%ROWTYPE INDEX BY BINARY_INTEGER;
          L_CNT T_CNT;

          /* VARIABLES */
          V_MOB_ID NUMBER(16);
          V_MRE_ID NUMBER(16);
        BEGIN
          /*
          * CONSTANTES
          */
          C_MASTER_SCHEMA := '${master.schema}';

          BEGIN
            EXECUTE IMMEDIATE 'SELECT MIN(DD_MOB_ID) FROM '||C_MASTER_SCHEMA||'.DD_MOB_MOTIVOS_BAJA WHERE DD_MOB_CODIGO=''OTR'''
            INTO V_MOB_ID;

            EXCEPTION WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20000,'NO EXISTE EL MOTVO DE BAJA  CODIGO=OTR');
          END;

          BEGIN
            EXECUTE IMMEDIATE 'SELECT MIN(DD_MRE_ID) FROM DD_MRE_MOB_RECHAZO_REC_FIC WHERE DD_MRE_CODIGO=''REL'''
            INTO V_MRE_ID;

            EXCEPTION WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20000,'NO EXISTE EL MOTVO DE RECHAZO  CODIGO=REL');
          END;

          /* INICIO BUCLE PRINCIPAL */
          OPEN C_SIN_REL;
            FETCH C_SIN_REL BULK COLLECT INTO L_CNT;

            FORALL I IN 1..L_CNT.COUNT
            INSERT INTO REC_FIC_RECHAZOS_CONTRATOS
            SELECT TRUNC(SYSDATE), V_MRE_ID, REC.*
            FROM REC_FICHERO_CONTRATOS REC
            WHERE RCF_AGE_ID = L_CNT(I).RCF_AGE_ID AND ID_ENVIO = L_CNT(I).ID_ENVIO;

            FORALL I IN 1..L_CNT.COUNT
            DELETE FROM REC_FICHERO_CONTRATOS WHERE RCF_AGE_ID = L_CNT(I).RCF_AGE_ID AND ID_ENVIO = L_CNT(I).ID_ENVIO;

            FORALL I IN 1..L_CNT.COUNT
            INSERT INTO TMP_REC_CONTRATOS_BAJA_MOT (RCF_AGE_ID, CNT_ID, DD_MOB_ID)
            VALUES(L_CNT(I).RCF_AGE_ID, L_CNT(I).CNT_ID, V_MOB_ID);

          CLOSE C_SIN_REL;
          /* FIN BUCLE PRINCIPAL */
          COMMIT;
        END;

        
        DECLARE
        /*
          * CONSTANTES
          */
          C_MASTER_SCHEMA VARCHAR2(50 CHAR);

          /* CURSOSRS */
          /*
           * Este cursor sirve para detectar las personas en REC_FICHERO que no tienen relaciones
           */
         CURSOR C_SIN_REL IS
          SELECT DISTINCT REC.RCF_AGE_ID,REC.CODIGO_PERSONA
          FROM REC_FICHERO_PERSONAS REC
          WHERE NOT EXISTS
            (SELECT 1
            FROM REC_FICHERO_CONTRATOS_PERSONAS
            WHERE RCF_AGE_ID = REC.RCF_AGE_ID
            AND CODIGO_PERSONA     = REC.CODIGO_PERSONA
            );

          TYPE T_PER IS TABLE OF C_SIN_REL%ROWTYPE INDEX BY BINARY_INTEGER;
          L_PER T_PER;

          /* VARIABLES */
          V_MRE_ID NUMBER(16);
        BEGIN
          /*
          * CONSTANTES
          */
          C_MASTER_SCHEMA := '${master.schema}';

          BEGIN
            EXECUTE IMMEDIATE 'SELECT MIN(DD_MRE_ID) FROM DD_MRE_MOB_RECHAZO_REC_FIC WHERE DD_MRE_CODIGO=''REL'''
            INTO V_MRE_ID;

            EXCEPTION WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20000,'NO EXISTE EL MOTVO DE RECHAZO  CODIGO=REL');
          END;

          /* INICIO BUCLE PRINCIPAL */
          OPEN C_SIN_REL;
            FETCH C_SIN_REL BULK COLLECT INTO L_PER;

            FORALL I IN 1..L_PER.COUNT
            INSERT INTO REC_FIC_RECHAZOS_PERSONAS
            SELECT TRUNC(SYSDATE), V_MRE_ID, REC.*
            FROM REC_FICHERO_PERSONAS REC
            WHERE RCF_AGE_ID = L_PER(I).RCF_AGE_ID AND CODIGO_PERSONA = L_PER(I).CODIGO_PERSONA;

            FORALL I IN 1..L_PER.COUNT
            DELETE FROM REC_FICHERO_PERSONAS WHERE RCF_AGE_ID = L_PER(I).RCF_AGE_ID AND CODIGO_PERSONA = L_PER(I).CODIGO_PERSONA;

          CLOSE C_SIN_REL;
          /* FIN BUCLE PRINCIPAL */
          COMMIT;
        END;


    begin
      OPERACION_DDL.DDL_TABLE('TRUNCATE','REC_FICHERO_CONTRATOS_BAJAS');
    end;


