INSERT INTO BATCH_RCF_ENTRADA SELECT ESQ.RCF_ESQ_ID AS RCF_ESQ_ID, ESQ.RCF_ESQ_PLAZO AS RCF_ESQ_PLAZO, sysdate AS RCF_ESQ_FECHA_LIB, -- Esto es para que arquetipe toda la cartera ESQ.BORRADO AS RCF_ESQ_BORRADO, 4 AS RCF_DD_EES_ID, ${ddEstadoEsquemas.Liberado.codigo} AS RCF_DD_EES_CODIGO, EES.BORRADO AS RCF_DD_EES_BORRADO, MTR.RCF_DD_MTR_ID AS RCF_DD_MTR_ID, MTR.RCF_DD_MTR_CODIGO AS RCF_DD_MTR_CODIGO, MTR.BORRADO AS RCF_DD_MTR_BORRADO, CAR.RCF_CAR_ID AS RCF_CAR_ID, CAR.RCF_CAR_NOMBRE AS RCF_CAR_NOMBRE, CAR.BORRADO AS RCF_CAR_BORRADO, CAST (null AS NUMBER(16)) AS RCF_DD_ECA_ID, --ECA.RCF_DD_ECA_ID , CAST (null AS NUMBER(16)) AS RCF_DD_ECA_CODIGO, --ECA.RCF_DD_ECA_CODIGO , CAST (null AS NUMBER(16)) AS RCF_DD_ECA_BORRADO, --ECA.BORRADO , RUL.RD_ID AS RD_ID, RUL.RD_NAME AS RD_NAME, RUL.RD_DEFINITION AS RD_DEFINITION, RUL.BORRADO AS RD_BORRADO, AGE.RCF_AGE_ID AS RCF_AGE_ID, AGE.RCF_AGE_NOMBRE AS RCF_AGE_NOMBRE, AGE.RCF_AGE_CODIGO AS RCF_AGE_CODIGO, AGE.BORRADO AS RCF_AGE_BORRADO, ESC.RCF_ESC_ID AS RCF_ESC_ID, ESC.RCF_ESC_PRIORIDAD AS RCF_ESC_PRIORIDAD, ESC.BORRADO AS RCF_ESC_BORRADO, TCE.DD_TCE_ID AS RCF_DD_TCE_ID, TCE.DD_TCE_CODIGO AS RCF_DD_TCE_CODIGO, TCE.BORRADO AS RCF_DD_TCE_BORRADO, TGC.DD_TGC_ID AS RCF_DD_TGC_ID, TGC.DD_TGC_CODIGO AS RCF_DD_TGC_CODIGO, TGC.BORRADO AS RCF_DD_TGC_BORRADO, AER.DD_AER_ID AS RCF_DD_AER_ID, AER.DD_AER_CODIGO AS RCF_DD_AER_CODIGO, AER.BORRADO AS RCF_DD_AER_BORRADO, SCA.RCF_SCA_ID AS RCF_SCA_ID, SCA.RCF_SCA_NOMBRE AS RCF_SCA_NOMBRE, SCA.RCF_SCA_PARTICION AS RCF_SCA_PARTICION, SCA.BORRADO AS RCF_SCA_BORRADO, TPR.RCF_DD_TPR_ID AS RCF_DD_TPR_ID, TPR.RCF_DD_TPR_CODIGO AS RCF_DD_TPR_CODIGO, TPR.BORRADO AS RCF_DD_TPR_BORRADO, ITV.RCF_ITV_ID AS RCF_ITV_ID, ITV.RCF_ITV_NOMBRE AS RCF_ITV_NOMBRE, ITV.RCF_ITV_FECHA_ALTA AS RCF_ITV_FECHA_ALTA, ITV.RCF_ITV_PLAZO_MAX AS RCF_ITV_PLAZO_MAX, ITV.RCF_ITV_NO_GEST AS RCF_ITV_NO_GEST, ITV.BORRADO AS RCF_ITV_BORRADO, MFA.RCF_MFA_ID AS RCF_MFA_ID, MFA.RCF_MFA_NOMBRE AS RCF_MFA_NOMBRE, MFA.BORRADO AS RCF_MFA_BORRADO, POA.RCF_POA_ID AS RCF_POA_ID, POA.RCF_POA_CODIGO AS RCF_POA_CODIGO, POA.BORRADO AS RCF_POA_BORRADO, MOR.RCF_MOR_ID AS RCF_MOR_ID, MOR.RCF_MOR_NOMBRE AS RCF_MOR_NOMBRE, MOR.BORRADO AS RCF_MOR_BORRADO, SUA.RCF_SUA_ID AS RCF_SUA_ID, SUA.RCF_SUA_COEFICIENTE AS RCF_SUA_COEFICIENTE, SUA.BORRADO AS RCF_SUA_BORRADO, SUR.RCF_SUR_ID AS RCF_SUR_ID, SUR.RCF_SUR_POSICION AS RCF_SUR_POSICION, SUR.RCF_SUR_PORCENTAJE AS RCF_SUR_PORCENTAJE, SUR.BORRADO AS RCF_SUR_BORRADO FROM RCF_ESQ_ESQUEMA ESQ JOIN RCF_DD_MTR_MODELO_TRANSICION MTR ON ESQ.RCF_DD_MTR_ID = MTR.RCF_DD_MTR_ID JOIN RCF_DD_EES_ESTADO_ESQUEMA EES ON ESQ.RCF_DD_EES_ID = EES.RCF_DD_EES_ID JOIN RCF_ESC_ESQUEMA_CARTERAS ESC ON ESQ.RCF_ESQ_ID = ESC.RCF_ESQ_ID AND ESC.BORRADO = 0 LEFT JOIN RCF_DD_TGC_TIPO_GESTION_CART TGC ON ESC.DD_TGC_ID = TGC.DD_TGC_ID JOIN RCF_DD_TCE_TIPO_CARTERA_ESQ TCE ON ESC.DD_TCE_ID = TCE.DD_TCE_ID LEFT JOIN RCF_DD_AER_AMBITO_EXP_REC AER ON ESC.DD_AER_ID = AER.DD_AER_ID JOIN RCF_CAR_CARTERA CAR ON ESC.RCF_CAR_ID = CAR.RCF_CAR_ID --JOIN RCF_DD_ECA_ESTADO_CARTERA ECA ON CAR.RCF_DD_ECA_ID = ECA.RCF_DD_ECA_ID JOIN RULE_DEFINITION RUL ON CAR.RD_ID = RUL.RD_ID LEFT JOIN RCF_SCA_SUBCARTERA SCA ON ESC.RCF_ESC_ID = SCA.RCF_ESC_ID AND SCA.BORRADO = 0 LEFT JOIN RCF_DD_TPR_TIPO_REPARTO_SUBC TPR ON SCA.RCF_DD_TPR_ID = TPR.RCF_DD_TPR_ID AND TPR.BORRADO = 0 LEFT JOIN RCF_ITV_ITI_METAS_VOLANTES ITV ON SCA.RCF_ITV_ID = ITV.RCF_ITV_ID AND ITV.BORRADO = 0 LEFT JOIN RCF_MFA_MODELOS_FACTURACION MFA ON SCA.RCF_MFA_ID = MFA.RCF_MFA_ID AND MFA.BORRADO = 0 LEFT JOIN RCF_SUA_SUBCARTERA_AGENCIAS SUA ON SCA.RCF_SCA_ID = SUA.RCF_SCA_ID AND SUA.BORRADO = 0 LEFT JOIN RCF_AGE_AGENCIAS AGE ON SUA.RCF_AGE_ID = AGE.RCF_AGE_ID AND AGE.BORRADO = 0 LEFT JOIN RCF_SUR_SUBCARTERA_RANKING SUR ON SCA.RCF_SCA_ID = SUR.RCF_SUR_ID AND SUR.BORRADO = 0 LEFT JOIN RCF_POA_POLITICA_ACUERDOS POA ON SCA.RCF_POA_ID = POA.RCF_POA_ID AND POA.BORRADO = 0 LEFT JOIN RCF_MOR_MODELO_RANKING MOR ON SCA.RCF_MOR_ID = MOR.RCF_MOR_ID WHERE ESQ.RCF_ESQ_ID = (SELECT RCF_ESQ_ID FROM (SELECT ESQ1.RCF_ESQ_ID , RANK() OVER (ORDER BY ESS.RCF_ESS_FECHA_PETICION DESC) fecha_rank FROM RCF_ESQ_ESQUEMA ESQ1 JOIN RCF_DD_EES_ESTADO_ESQUEMA EES1 ON ESQ1.RCF_DD_EES_ID = EES1.RCF_DD_EES_ID JOIN RCF_ESS_ESQUEMA_SIMULACION ESS ON ESQ1.RCF_ESQ_ID = ESS.RCF_ESQ_ID WHERE ESQ1.BORRADO = 0 AND EES1.BORRADO = 0 AND EES1.RCF_DD_EES_CODIGO = 'PTS') WHERE ROWNUM = 1); COMMIT;
OPERACION_DDL.DDL_TABLE('STATS','BATCH_RCF_ENTRADA');
End;
