declare
		nCount NUMBER;
		v_sql LONG;
		begin
		SELECT COUNT(*) INTO NCOUNT FROM ALL_TABLES WHERE TABLE_NAME = 'TMP_REC_EXP_SIN_PERSONAS';
		IF(nCount = 0) THEN
      	v_sql:='
        	CREATE TABLE TMP_REC_EXP_SIN_PERSONAS (
            	EXP_ID          NUMBER(16) NOT NULL
        	)
      	';
  		execute immediate v_sql;
		END IF;
end;

