/*
 * INSERT TMP_REC_EXP_AGE_CNT_EXC
 */

INSERT INTO TMP_REC_CONTRATOS_BAJA_MOT
SELECT CNT.CNT_ID, TRUNC(DBMS_RANDOM.VALUE(1,5)), MOB.DD_MOB_ID
FROM BANKMASTER.DD_MOB_MOTIVOS_BAJA MOB
  JOIN (SELECT * FROM CNT_CONTRATOS WHERE CNT_ID NOT IN (SELECT CNT_ID FROM TMP_REC_EXP_AGE_CNT_EXC) AND ROWNUM <= 100) CNT ON  MOB.DD_MOB_ID = MOD(CNT.CNT_ID, 7);

COMMIT;



