--UPDATE DD_TAC_TIPO_ACTUACION SET BORRADO = 1 WHERE DD_TAC_ID = 5;
--UPDATE DD_TPO_TIPO_PROCEDIMIENTO SET BORRADO = 1 WHERE DD_TAC_ID = 5 AND DD_TPO_CODIGO = 'P54';
UPDATE DD_TPO_TIPO_PROCEDIMIENTO SET DD_TAC_ID = 8 WHERE DD_TPO_CODIGO = 'P54';


update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; consignar el porcentaje por el cual se ha adjudicado la entidad el bien junto a la fecha en la cual se ha producido dicha adjudicaci&'||'oacute;n.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Registrar solicitud de posesi&'||'oacute;n&'||'quot;.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_regPorcentajePropiedadAdjudicada');
            
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; consignar la fecha en la cual queda registrada la solicitud de posesi&'||'oacute;n de bien.</p><p style="margin-bottom: 10px">En el campo "Ocupado" deber&'||'aacute; indicar si el bien es ocupado por alguna persona actualmente, ya sea el ejecutado o terceras personas.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Solicitar lanzamiento&'||'quot; en el caso de que haya indicado que el bien se enceuntra ocupado, para el caso de que no este ocupado la siguiente tarea ser&'||'aacute; "Registrar posesi&'||'oacute;n"</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_regSolicitudPosesion');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; consignar la fecha en la que realizar la solicitud de lanzamiento.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; "Confirmar admisi&'||'oacute;n lanzamiento".</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_solicitarLanzamiento');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; consignar la fecha en la que se nos confirma la admisi&'||'oacute;n del lanzammiento.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; "Notificar a ocupantes".</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_confirmarAdmisionLanzamiento');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; consignar la fecha en la cual se consigue notificar a los ocupantes del bien. En el caso de no haber conseguido notificar se indicar&'||'aacute; en el campo correspondiente, no siendo obligado introducir la fecha.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Registrar vista&'||'quot; en el caso de que haya podido notificar a los ocupantes del bien, en caso contrario se iniciar&'||'aacute; un tr&'||'aacute;mite de notificaci&'||'oacute;n.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_notificarAOcupantes');
            
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">En esta pantalla deber&'||'aacute; de consignar la fecha de celebraci&'||'oacute;n de la vista.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; "Registrar vista".</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_registrarVista');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">En &'||'eacute;sta pantalla se deber&'||'aacute; de consignar la fecha de notificaci&'||'oacute;n de la Resoluci&'||'oacute;n que hubiere reca&'||'iacute;do como consecuencia de la vista celebrada.</p><p style="margin-bottom: 10px">Se indicar&'||'aacute; si el resultado de dicha resoluci&'||'oacute;n ha sido favorable para los intereses de la entidad o no.</p><p style="margin-bottom: 10px">Para el supuesto de que la resoluci&'||'oacute;n no fuere favorable para la entidad, deber&'||'aacute; comunicar dicha circunstancia al responsable interno de la misma a traves del bot&'||'oacute;n &'||'quot;Comunicaci&'||'oacute;n&'||'quot;. Una vez reciba la aceptaci&'||'oacute;n del supervisor deber&'||'aacute; gestionar el recurso por medio de la pestaña &'||'quot;Recursos&'||'quot;.</p><p style="margin-bottom: 10px">Para el supuesto de anuncio del recurso por la parte contraria se deber&'||'aacute; gestionar directamente a trav&'||'eacute;s de la pestaña &'||'quot;Recursos&'||'quot;.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene &'||'eacute;sta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Registrar lanzamiento efectivo&'||'quot;.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_registrarResolucion');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; consignar la fecha en la cual queda registrada la posesi&'||'oacute;n de bien.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene esta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Registrar lanzamiento efectivo&'||'quot;.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_registrarPosesion');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; registrar la fecha de lanzamiento efectivo del bien as&'||'iacute; como la fecha de posesi&'||'oacute;n del mismo.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene &'||'eacute;sta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Registrar cambio de cerradura&'||'quot;.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_registrarLanzamientoEfectivo');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; registrar la fecha en la que se cambi&'||'oacute; la cerradura del bien adjudicado.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene &'||'eacute;sta pantalla la siguiente tarea ser&'||'aacute; &'||'quot;Registrar persona depositaria de la llaves&'||'quot;.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_registrarCambioCerradura');
      
update TFI_TAREAS_FORM_ITEMS tfi set tfi.TFI_LABEL = '<div align="justify" style="font-size: 8pt; font-family: Arial; margin-bottom: 30px;"><p style="margin-bottom: 10px">A trav&'||'eacute;s de esta pantalla deber&'||'aacute; registrar la fecha en la que se dan las llaves a la persona depositaria as&'||'iacute; como el nombre de dicha persona.</p><p style="margin-bottom: 10px">En el campo observaciones consignar cualquier aspecto relevante que le interesa quede reflejado en ese punto del procedimiento.</p><p style="margin-bottom: 10px">Una vez rellene &'||'eacute;sta pantalla se le abrir&'||'aacute; una tarea en la que propondr&'||'aacute;, seg&'||'uacute;n su criterio, la siguiente actuaci&'||'oacute;n al responsable de la entidad.</p></div>' 
WHERE tfi.TFI_NOMBRE = 'titulo' and
      tfi.tap_id = ( select tap_id from tap_tarea_procedimiento where TAP_CODIGO = 'P54_registrarPersonaDepLlaves');
  