               T_JBV('cnt-00.countValidator',1,'select count(1) from tmp_cnt_contratos  ',1,2,0)
             , T_JBV('cnt-01.entityValidator',1,'SELECT tmp_cnt_cod_entidad AS ERROR_FIELD,  (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE   FROM tmp_cnt_contratos X      WHERE tmp_cnt_cod_entidad <> #TOKEN_ENTITY#       ',1,2,0)
             , T_JBV('cnt-02.loadDateValidator.Oracle9iDialect',1,'SELECT TMP_CNT_FECHA_EXTRACCION AS ERROR_FIELD,       (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE  from TMP_CNT_CONTRATOS X where TMP_CNT_FECHA_EXTRACCION <> to_date(#TOKEN_DATE#,''''YYYYMMDD'''')   ',1,2,0)
