             , T_JBV('cnt-04.insertOfficeZoneCodeValidator',1,'DECLARE CURSOR c_NEWOFFICE IS SELECT DISTINCT TMP_CNT_COD_ENT_OFI_CNTBLE, TMP_CNT_COD_OFI_CNTBLE, TMP_CNT_COD_SUBSC_OFI_CNTBLE, TMP_CNT_COD_OFICINA FROM tmp_cnt_contratos X WHERE NOT EXISTS ( SELECT 1 FROM ofi_oficinas o WHERE X.TMP_CNT_COD_OFICINA = O.OFI_CODIGO_ENTIDAD_OFICINA||LPAD(O.OFI_CODIGO_OFICINA, 5, ''''0'''')||LPAD(O.OFI_CODIGO_SUBSECCION_OFICINA, 2, ''''0'''') ); v_SQL VARCHAR2(32000); v_SQLInsert VARCHAR2(32000); n_OFI_ID NUMBER;  n_OFI_CODE VARCHAR2 (100); n_ZON_ID NUMBER;  v_ZON_COD VARCHAR2(50);  n_ZON_PID NUMBER;  ifExist NUMBER; BEGIN  FOR i IN c_NEWOFFICE LOOP  v_SQL := ''''--> VALORES DEL LOOP ''''||i.TMP_CNT_COD_ENT_OFI_CNTBLE||'''' - ''''||i.TMP_CNT_COD_OFI_CNTBLE||'''' - ''''||i.TMP_CNT_COD_SUBSC_OFI_CNTBLE||''''.''''; select S_OFI_OFICINAS.NEXTVAL into n_OFI_ID from dual; select i.TMP_CNT_COD_OFICINA into n_OFI_CODE FROM DUAL; select count(1) into ifExist FROM OFI_OFICINAS where OFI_CODIGO = i.TMP_CNT_COD_OFICINA; if ifExist = 0 then v_SQLInsert := ''''Insert into OFI_OFICINAS ( OFI_ID, OFI_CODIGO, OFI_NOMBRE, VERSION, USUARIOCREAR, FECHACREAR, BORRADO, OFI_FECHA_EXTRACCION, OFI_FECHA_DATO, OFI_CODIGO_ENTIDAD_OFICINA, OFI_CODIGO_OFICINA, OFI_CODIGO_SUBSECCION_OFICINA ) SELECT ''''||n_OFI_ID||'''', ''''''''''''||n_OFI_CODE||'''''''''''', ''''''''Oficina pendiente de definir'''''''', ''''''''0'''''''', ''''''''Batch'''''''',sysdate, ''''''''0'''''''', sysdate, sysdate, ''''||i.TMP_CNT_COD_ENT_OFI_CNTBLE||'''',''''||i.TMP_CNT_COD_OFI_CNTBLE||'''',''''||i.TMP_CNT_COD_SUBSC_OFI_CNTBLE||'''' FROM DUAL WHERE ''''''''''''||n_OFI_CODE||'''''''''''' NOT IN (SELECT DISTINCT OFI_CODIGO FROM OFI_OFICINAS)  ''''; EXECUTE IMMEDIATE v_SQLInsert; select s_ZON_ZONIFICACION.nextval into n_ZON_ID from dual;  select to_char(''''01''''||newzoncod) zoncod into v_ZON_COD from ( select max(hijo)+1 newzoncod from (select zon_cod, to_number(substr(zon_cod, 3, 6)) hijo from zon_zonificacion where length(zon_cod) = 6 ) ); SELECT ZON_ID into n_ZON_PID FROM ZON_ZONIFICACION WHERE ZON_DESCRIPCION = ''''CENTRO EMPRESA''''; v_SQLInsert := ''''Insert into ZON_ZONIFICACION ( ZON_ID, ZON_COD, ZON_PID, NIV_ID, OFI_ID, VERSION, USUARIOCREAR,FECHACREAR, BORRADO, ZON_FECHA_EXTRACCION,ZON_FECHA_DATO ) SELECT ''''||n_ZON_ID||'''', ''''''''''''||v_ZON_COD||'''''''''''', ''''||n_ZON_PID||'''', (SELECT NIV_ID FROM niv_nivel WHERE niv_descripcion = ''''''''Oficina'''''''' and borrado = 0), ''''||n_OFI_ID||'''', ''''''''0'''''''', ''''''''Batch'''''''',sysdate, ''''''''0'''''''', sysdate, sysdate FROM DUAL ''''; EXECUTE IMMEDIATE v_SQLInsert; end if; END LOOP; END; ',1,1,0)
             , T_JBV('cnt-04.officeCodeValidator',1,'SELECT TMP_CNT_COD_OFICINA AS ERROR_FIELD,   (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE  FROM tmp_cnt_contratos X   WHERE NOT EXISTS(SELECT 1      FROM ofi_oficinas o      WHERE X.TMP_CNT_COD_OFICINA = O.OFI_CODIGO_ENTIDAD_OFICINA||LPAD(O.OFI_CODIGO_OFICINA, 5, ''''0'''')||LPAD(O.OFI_CODIGO_SUBSECCION_OFICINA, 2, ''''0'''')     )   ',1,2,0)
             , T_JBV('cnt-05.zonaCodeValidator',1,'SELECT X.TMP_CNT_COD_ENT_OFI_CNTBLE||''''|''''||X.TMP_CNT_COD_OFI_CNTBLE||''''|''''||X.TMP_CNT_COD_SUBSC_OFI_CNTBLE AS ERROR_FIELD,      (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE   FROM tmp_cnt_contratos X   WHERE NOT EXISTS(SELECT 1      FROM zon_zonificacion z, ofi_oficinas o     where O.OFI_CODIGO_ENTIDAD_OFICINA||LPAD(O.OFI_CODIGO_OFICINA, 5, ''''0'''')||LPAD(O.OFI_CODIGO_SUBSECCION_OFICINA, 2, ''''0'''') = X.TMP_CNT_COD_OFICINA     and z.ofi_id = o.ofi_id    )   ',1,2,0)
             , T_JBV('cnt-06.insertCurrencyCodeValidator',1,'INSERT INTO dd_mon_monedas (DD_MON_ID, DD_MON_CODIGO, DD_MON_DESCRIPCION, USUARIOCREAR, FECHACREAR, BORRADO)    SELECT S_dd_mon_monedas.NEXTVAL ,MONX.DD_MON_CODIGO ,MONX.DD_MON_DESCRIPCION ,#TOKEN_USR#  ,SYSTIMESTAMP ,0   FROM (      SELECT DISTINCT ERROR_FIELD as DD_MON_CODIGO, ''''Moneda pendiente de definir (''''||ERROR_FIELD||'''')'''' as DD_MON_DESCRIPCION       from (      SELECT TMP_CNT_MONEDA AS ERROR_FIELD,      (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE       FROM tmp_cnt_contratos X      WHERE NOT EXISTS (SELECT 1 FROM dd_mon_monedas m WHERE X.TMP_CNT_MONEDA = m.DD_MON_CODIGO)    )   ) MONX  ',1,1,0)
             , T_JBV('cnt-06.currencyCodeValidator',1,'SELECT TMP_CNT_MONEDA AS ERROR_FIELD,  (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE   FROM tmp_cnt_contratos X  WHERE NOT EXISTS (SELECT 1      FROM dd_mon_monedas m       WHERE X.TMP_CNT_MONEDA = m.DD_MON_CODIGO)       ',1,2,0)
             , T_JBV('cnt-07.insertProductTypeCodeValidator',1,'INSERT INTO DD_TPE_TIPO_PROD_ENTIDAD (DD_TPE_ID, DD_TPR_ID, DD_TPE_CODIGO, DD_TPE_ACTIVO, DD_TPE_DESCRIPCION, USUARIOCREAR, FECHACREAR, BORRADO)   SELECT      S_DD_TPE_TIPO_PROD_ENTIDAD.NEXTVAL     ,(SELECT DD_TPR_ID FROM DD_TPR_TIPO_PROD TPR WHERE UPPER(TPR.DD_TPR_DESCRIPCION) LIKE ''''%PRODUCTO NO ESPECIFICADO%'''')      ,TPEX.DD_TPE_CODIGO      ,1     ,TPEX.DD_TPE_DESCRIPCION     ,#TOKEN_USR#      ,SYSTIMESTAMP      ,0   FROM (       SELECT DISTINCT ERROR_FIELD as DD_TPE_CODIGO, ''''Tipo producto pendiente de definir (''''||ERROR_FIELD||'''')'''' as DD_TPE_DESCRIPCION from (       SELECT X.TMP_CNT_TIPO_PRODUCTO AS ERROR_FIELD,     (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE     FROM TMP_CNT_CONTRATOS X     WHERE NOT EXISTS (SELECT 1 FROM DD_TPE_TIPO_PROD_ENTIDAD TPE WHERE X.TMP_CNT_TIPO_PRODUCTO = TRIM(TPE.DD_TPE_CODIGO))      ) ) TPEX      ',1,1,0)
             , T_JBV('cnt-07.productTypeCodeValidator',1,'SELECT tmp_cnt_tipo_producto AS ERROR_FIELD,       (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE FROM tmp_cnt_contratos X WHERE NOT EXISTS (SELECT 1    FROM dd_tpe_tipo_prod_entidad tpe     where trim(X.tmp_cnt_tipo_producto) = trim(tpe.DD_TPE_CODIGO)    )       ',1,2,0)
             , T_JBV('cnt-08.contratoRelacionValidator',1,'SELECT ''''No existe relacion'''' AS ERROR_FIELD,  (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE   from tmp_cnt_contratos X  where not exists (select 1    from tmp_cnt_per tcp    where tcp.TMP_CNT_PER_COD_PROPIETARIO = X.TMP_CNT_CODIGO_PROPIETARIO      AND tcp.TMP_CNT_PER_TIPO_PRODUCTO = X.TMP_CNT_TIPO_PRODUCTO      AND X.TMP_CNT_CONTRATO = tcp.TMP_CNT_PER_NUM_CONTRATO       )  ',1,2,0)
             , T_JBV('cnt-09.movimientoPrevio',1,'SELECT cnt.cnt_id AS ERROR_FIELD,    (cnt_cod_entidad ||''''-''''|| cnt.CNT_CONTRATO)AS ENTITY_CODE   FROM cnt_contratos cnt, #TOKEN_SchemaMaster#.DD_ESC_ESTADO_CNT ec   WHERE CNT.BORRADO = 0  AND CNT.DD_ESC_ID = ec.DD_ESC_ID  AND ec.dd_esc_codigo = #TOKEN_ENTITY#  AND NOT EXISTS(SELECT 1   FROM TMP_CNT_CONTRATOS X   WHERE LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''') = cnt.CNT_CONTRATO   )      ',1,2,0)
             , T_JBV('cnt-10.fechaExtracionMenorHoy',1,'SELECT X.tmp_cnt_fecha_extraccion AS ERROR_FIELD,      (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE   FROM tmp_cnt_contratos X  WHERE X.tmp_cnt_fecha_extraccion > #TOKEN_ActualDate#      ',1,2,0)
             , T_JBV('cnt-11.fechaPosVencida',1,'SELECT X.tmp_cnt_fecha_pos_vencida AS ERROR_FIELD,      (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE        FROM tmp_cnt_contratos X, cnt_contratos c, mov_movimientos m      WHERE LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''') = c.cnt_contrato      AND c.cnt_id = m.cnt_id      AND X.tmp_cnt_fecha_pos_vencida < m.mov_fecha_pos_vencida  ',1,2,0)
             , T_JBV('cnt-12.activoPositvo',1,'SELECT X.TMP_CNT_POS_VIVA_NO_VENCIDA AS ERROR_FIELD,      (tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE        FROM tmp_cnt_contratos X, dd_tpe_tipo_prod_entidad tp      WHERE TRIM(tp.dd_tpe_codigo) = TRIM(X.TMP_CNT_TIPO_PRODUCTO)      AND tp.dd_tpe_activo=1      AND X.TMP_CNT_POS_VIVA_VENCIDA < 0     ',1,2,0)
             , T_JBV('cnt-13.insertFinalidadOficial',1,'INSERT INTO DD_FNO_FINALIDAD_OFICIAL (DD_FNO_ID, DD_FNO_CODIGO, DD_FNO_DESCRIPCION, USUARIOCREAR, FECHACREAR, BORRADO)    SELECT S_DD_FNO_FINALIDAD_OFICIAL.NEXTVAL       ,FNOX.DD_FNO_CODIGO       ,FNOX.DD_FNO_DESCRIPCION       ,#TOKEN_USR# ,SYSTIMESTAMP       ,0   FROM (    SELECT DISTINCT ERROR_FIELD as DD_FNO_CODIGO, ''''Finalidad pendiente de definir (''''||ERROR_FIELD||'''')'''' as DD_FNO_DESCRIPCION     from (     SELECT  X.TMP_CNT_FINALIDAD_OFI AS ERROR_FIELD       ,(tmp_cnt_cod_entidad ||''''-''''|| LPAD(TO_CHAR(X.TMP_CNT_CODIGO_PROPIETARIO),5,''''0'''')||LPAD(X.TMP_CNT_TIPO_PRODUCTO,5,''''0'''')||LPAD(TO_CHAR(X.tmp_cnt_contrato),17,''''0'''')||LPAD(TO_CHAR(X.TMP_CNT_NUM_ESPEC),15,''''0'''')) AS ENTITY_CODE       FROM TMP_CNT_CONTRATOS X     WHERE X.TMP_CNT_FINALIDAD_OFI is not null       AND NOT EXISTS (SELECT 1 FROM DD_FNO_FINALIDAD_OFICIAL FNO WHERE X.TMP_CNT_FINALIDAD_OFI = FNO.DD_FNO_CODIGO)    )   ) FNOX     ',1,1,0)
