sql.value:
declare
		nCount NUMBER;
		v_sql LONG;
		begin
		SELECT COUNT(*) INTO NCOUNT FROM ALL_TABLES WHERE TABLE_NAME = 'CPR_COBROS_PAGOS_RECOBRO';
		IF(nCount <= 0) THEN
      v_sql:='
		CREATE TABLE CPR_COBROS_PAGOS_RECOBRO (
		    CPR_ID          	NUMBER(16) NOT NULL,
			CPA_ID          	NUMBER(16) NOT NULL,
			CPA_FECHA       	DATE NOT NULL,
			CNT_ID          	NUMBER(16) NOT NULL,
			EXP_ID          	NUMBER(16) NOT NULL,
			RCF_AGE_ID      	NUMBER(16) NOT NULL,
			RCF_SCA_ID			NUMBER(16) NOT NULL 
		)
      ';
  		execute immediate v_sql;
  		v_sql:='
  			CREATE SEQUENCE S_CPR_COBROS_PAGOS_RECOBRO INCREMENT BY 1 START WITH 1
  		';
  		execute immediate v_sql;
		END IF;
END;

