THEN
v_sql:='
	CREATE TABLE TMP_REC_CNT_LIBRES_ARQ_REC (
			CNT_ID          NUMBER(16) NOT NULL,
		    PER_ID          NUMBER(16) NOT NULL,
			ARQ_ID	    	NUMBER(16) NOT NULL,
			MOV_RIESGO	    NUMBER(14,2) NOT NULL
		)
';
execute immediate v_sql;
END IF;
end;

