sql.value:
declare
nCount NUMBER;
v_sql LONG;
begin
SELECT count(*) into nCount FROM all_tables where table_name = 'TMP_VALIDACION';
IF(nCount = 1)
THEN
v_sql:='DROP TABLE TMP_VALIDACION';
execute immediate v_sql;
END IF;
end;

